import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import '../styles/main.css';


const Layout = ({ children }) => {
  const { user, logout, isAuthenticated } = useAuth();
  const location = useLocation();
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  // ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿπŸÜÿØ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÖÿ≥ÿßÿ±
  useEffect(() => {
    setIsMenuOpen(false);
    setIsDropdownOpen(false);
  }, [location]);

  // ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿπŸÜÿØ ÿßŸÑŸÜŸÇÿ± ÿÆÿßÿ±ÿ¨Ÿáÿß
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (isMenuOpen && !event.target.closest('.nav-menu') && !event.target.closest('.menu-toggle')) {
        setIsMenuOpen(false);
      }
    };

    document.addEventListener('click', handleClickOutside);
    return () => document.removeEventListener('click', handleClickOutside);
  }, [isMenuOpen]);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const getRoleText = (role) => {
    switch(role) {
      case 'admin': return 'ÿßŸÑŸÖÿØŸäÿ± ÿßŸÑÿπÿßŸÖ';
      case 'branch_manager': return 'ŸÖÿØŸäÿ± ŸÅÿ±ÿπ';
      case 'employee': return 'ŸÖŸàÿ∏ŸÅ';
      default: return 'ŸÖÿ≥ÿ™ÿÆÿØŸÖ';
    }
  };

  const getRoleColor = (role) => {
    switch(role) {
      case 'admin': return 'bg-gradient-to-r from-red-500 to-pink-500';
      case 'branch_manager': return 'bg-gradient-to-r from-green-500 to-emerald-500';
      case 'employee': return 'bg-gradient-to-r from-blue-500 to-cyan-500';
      default: return 'bg-gradient-to-r from-gray-500 to-gray-600';
    }
  };

  if (!isAuthenticated) {
    return children;
  }

  const navItems = {
    admin: [
      { path: '/admin', label: 'ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ', icon: 'üè†' },
      { path: '/admin/users', label: 'ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ', icon: 'üë•' },
      { path: '/admin/branches', label: 'ÿßŸÑŸÅÿ±Ÿàÿπ', icon: 'üè¨' },
      { path: '/admin/games', label: 'ÿßŸÑÿ£ŸÑÿπÿßÿ®', icon: 'üéÆ' },
      { path: '/admin/rentals', label: 'ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™', icon: '‚è±Ô∏è' },
      { path: '/admin/activities', label: 'ÿ≥ÿ¨ŸÑ ÿßŸÑÿ£ŸÜÿ¥ÿ∑ÿ©', icon: 'üìä' },
      { path: '/admin/settings', label: 'ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™', icon: '‚öôÔ∏è' },
    ],
    branch_manager: [
      { path: '/employee', label: 'ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ', icon: 'üè†' },
      { path: '/employee/games', label: 'ÿßŸÑÿ£ŸÑÿπÿßÿ®', icon: 'üéÆ' },
      { path: '/employee/rentals', label: 'ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™', icon: '‚è±Ô∏è' },
      { path: '/employee/reports', label: 'ÿßŸÑÿ™ŸÇÿßÿ±Ÿäÿ±', icon: 'üìà' },
    ],
    employee: [
      { path: '/employee', label: 'ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ', icon: 'üè†' },
      { path: '/employee/games', label: 'ÿßŸÑÿ£ŸÑÿπÿßÿ®', icon: 'üéÆ' },
      { path: '/employee/rentals', label: 'ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™', icon: '‚è±Ô∏è' },
    ],
  };

  const userNavItems = navItems[user?.role] || navItems.employee;

  return (
    <div className="app-container">
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-container">
          {/* ÿßŸÑŸÇÿ≥ŸÖ ÿßŸÑÿ£Ÿäÿ≥ÿ±: ÿßŸÑÿ¥ÿπÿßÿ± */}
          <div className="flex items-center">
            <button 
              className="menu-toggle lg:hidden mr-4"
              onClick={toggleMenu}
              aria-label="ŸÅÿ™ÿ≠/ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑŸÇÿßÿ¶ŸÖÿ©"
            >
              {isMenuOpen ? '‚úï' : '‚ò∞'}
            </button>
            
            <Link to={user?.role === 'admin' ? '/admin' : '/employee'} className="navbar-brand">
<div className="logo">
  <img 
    src="/images/logol3bty.jpg" 
    alt="L3BTY Store Logo" 
    className="w-full h-full object-contain"
    onError={(e) => {
      e.target.onerror = null;
      e.target.src = "/images/logo-fallback.jpg";
      e.target.alt = "L3BTY Logo";
      e.target.style.backgroundColor = "#4361ee";
      e.target.style.padding = "8px";
      e.target.style.borderRadius = "8px";
    }}
  />
</div>              <div>
                <div className="brand-text">L3BTY Store</div>
                <div className="text-xs text-gray-500">ŸÜÿ∏ÿßŸÖ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™</div>
              </div>
            </Link>
          </div>

          {/* ÿßŸÑŸÇÿ≥ŸÖ ÿßŸÑÿ£ŸäŸÖŸÜ: ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ŸàÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ */}
          <div className="flex items-center gap-4">
            {/* ÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ */}
            <button className="relative p-2 text-gray-600 hover:text-gray-900">
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6.002 6.002 0 00-4-5.659V5a2 2 0 10-4 0v.341C7.67 6.165 6 8.388 6 11v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9" />
              </svg>
              <span className="absolute top-0 right-0 w-2 h-2 bg-red-500 rounded-full"></span>
            </button>

            {/* ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ */}
            <div className="relative">
              <button
                onClick={toggleDropdown}
                className="flex items-center gap-3 p-2 rounded-lg hover:bg-gray-100 transition-colors"
              >
                <div className="text-right">
                  <div className="font-medium text-gray-800">{user?.name || 'ŸÖÿ≥ÿ™ÿÆÿØŸÖ'}</div>
                  <div className="text-sm text-gray-500">{getRoleText(user?.role)}</div>
                </div>
                <div className={`w-10 h-10 rounded-full flex items-center justify-center text-white ${getRoleColor(user?.role)}`}>
                  {user?.name?.charAt(0) || 'U'}
                </div>
                <svg 
                  className={`w-5 h-5 text-gray-500 transition-transform ${isDropdownOpen ? 'rotate-180' : ''}`} 
                  fill="none" 
                  stroke="currentColor" 
                  viewBox="0 0 24 24"
                >
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                </svg>
              </button>

              {/* Dropdown Menu */}
              {isDropdownOpen && (
                <div className="absolute left-0 mt-2 w-48 bg-white rounded-lg shadow-xl border border-gray-200 py-1 z-50">
                  <div className="px-4 py-3 border-b border-gray-100">
                    <div className="font-medium text-gray-800">{user?.name}</div>
                    <div className="text-sm text-gray-500">{user?.email}</div>
                  </div>
                  
                  <Link
                    to="/profile"
                    className="flex items-center gap-2 px-4 py-3 text-gray-700 hover:bg-gray-100"
                  >
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                    </svg>
                    ÿßŸÑŸÖŸÑŸÅ ÿßŸÑÿ¥ÿÆÿµŸä
                  </Link>
                  
                  <Link
                    to="/settings"
                    className="flex items-center gap-2 px-4 py-3 text-gray-700 hover:bg-gray-100"
                  >
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10.325 4.317c.426-1.756 2.924-1.756 3.35 0a1.724 1.724 0 002.573 1.066c1.543-.94 3.31.826 2.37 2.37a1.724 1.724 0 001.065 2.572c1.756.426 1.756 2.924 0 3.35a1.724 1.724 0 00-1.066 2.573c.94 1.543-.826 3.31-2.37 2.37a1.724 1.724 0 00-2.572 1.065c-.426 1.756-2.924 1.756-3.35 0a1.724 1.724 0 00-2.573-1.066c-1.543.94-3.31-.826-2.37-2.37a1.724 1.724 0 00-1.065-2.572c-1.756-.426-1.756-2.924 0-3.35a1.724 1.724 0 001.066-2.573c-.94-1.543.826-3.31 2.37-2.37.996.608 2.296.07 2.572-1.065z" />
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                    </svg>
                    ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™
                  </Link>
                  
                  <div className="border-t border-gray-100"></div>
                  
                  <button
                    onClick={handleLogout}
                    className="flex items-center gap-2 w-full px-4 py-3 text-red-600 hover:bg-red-50"
                  >
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" />
                    </svg>
                    ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ© ŸÑŸÑŸÄ Mobile */}
        <div className={`nav-menu ${isMenuOpen ? 'active' : ''} lg:hidden`}>
          <div className="space-y-1">
            {userNavItems.map((item) => (
              <Link
                key={item.path}
                to={item.path}
                className={`flex items-center gap-3 px-4 py-3 rounded-lg transition-colors ${
                  location.pathname === item.path
                    ? 'bg-blue-50 text-blue-600'
                    : 'text-gray-700 hover:bg-gray-100'
                }`}
              >
                <span className="text-xl">{item.icon}</span>
                <span className="font-medium">{item.label}</span>
              </Link>
            ))}
          </div>
        </div>
      </nav>

      {/* ÿßŸÑŸÖÿ≠ÿ™ŸàŸâ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä */}
      <main className="main-content">
        {children}
      </main>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <div>
            <h3 className="footer-section-title">ÿπŸÜ ÿßŸÑŸÜÿ∏ÿßŸÖ</h3>
            <p className="text-gray-400 mt-2">
              ŸÜÿ∏ÿßŸÖ ÿ•ÿØÿßÿ±ÿ© ŸÖÿ™ŸÉÿßŸÖŸÑ ŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸäÿ©ÿå ŸÖÿµŸÖŸÖ ŸÑÿ™ÿ®ÿ≥Ÿäÿ∑ ÿπŸÖŸÑŸäÿßÿ™ ÿßŸÑÿ•ÿØÿßÿ±ÿ© ŸàÿßŸÑŸÖÿ™ÿßÿ®ÿπÿ©.
            </p>
          </div>

          
          <div>
            <h3 className="footer-section-title">ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑÿßÿ™ÿµÿßŸÑ</h3>
            <ul className="footer-links">
              <li className="flex items-center gap-2">
                <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M5.05 4.05a7 7 0 119.9 9.9L10 18.9l-4.95-4.95a7 7 0 010-9.9zM10 11a2 2 0 100-4 2 2 0 000 4z" clipRule="evenodd" />
                </svg>
                ÿßŸÑŸÇÿßŸáÿ±ÿ©ÿå ŸÖÿµÿ±
              </li>
              <li className="flex items-center gap-2">
                <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
                  <path d="M2 3a1 1 0 011-1h2.153a1 1 0 01.986.836l.74 4.435a1 1 0 01-.54 1.06l-1.548.773a11.037 11.037 0 006.105 6.105l.774-1.548a1 1 0 011.059-.54l4.435.74a1 1 0 01.836.986V17a1 1 0 01-1 1h-2C7.82 18 2 12.18 2 5V3z" />
                </svg>
                01016904292
              </li>
              <li className="flex items-center gap-2">
                <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
                  <path d="M2.003 5.884L10 9.882l7.997-3.998A2 2 0 0016 4H4a2 2 0 00-1.997 1.884z" />
                  <path d="M18 8.118l-8 4-8-4V14a2 2 0 002 2h12a2 2 0 002-2V8.118z" />
                </svg>
                support@l3bty.com
              </li>
            </ul>
          </div>
        </div>
        
        <div className="footer-bottom">
          <p>¬© {new Date().getFullYear()} L3BTY Store. ÿ¨ŸÖŸäÿπ ÿßŸÑÿ≠ŸÇŸàŸÇ ŸÖÿ≠ŸÅŸàÿ∏ÿ©.</p>
          <p className="mt-2 text-sm text-gray-500">ÿßŸÑÿ•ÿµÿØÿßÿ± 2.0.0 | ÿ¢ÿÆÿ± ÿ™ÿ≠ÿØŸäÿ´: {new Date().toLocaleDateString('ar-EG')}</p>
        </div>
      </footer>
    </div>
  );
};

export default Layout;
