import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const Rentals = () => {
  // ==================== CONTEXT & STATES ====================
  const { user } = useAuth();
  
  // States Ø§Ù„Ø£Ø³Ø§Ø³ÙŠØ©
  const [branch, setBranch] = useState(null);
  const [loading, setLoading] = useState(true);
  const [loadingMessage, setLoadingMessage] = useState('Ø¬Ø§Ø±ÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª...');
  const [currentTime, setCurrentTime] = useState('');
  const [currentEgyptTime, setCurrentEgyptTime] = useState('');

  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ ÙˆØ§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
  const [games, setGames] = useState([]);
  const [gameDurations, setGameDurations] = useState({});
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  
  // Ù†Ø§ÙØ°Ø§Øª Ù…Ø®ØªÙ„ÙØ©
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showCancelModal, setShowCancelModal] = useState(false);
  const [showEndModal, setShowEndModal] = useState(false);
  const [showChangeGameModal, setShowChangeGameModal] = useState(false);
  const [showReRentalModal, setShowReRentalModal] = useState(false);
  const [showOpenTimeActions, setShowOpenTimeActions] = useState(false);
  const [showAddMoreGames, setShowAddMoreGames] = useState(false);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù†Ù…Ø§Ø°Ø¬
  const [selectedGames, setSelectedGames] = useState([]);
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');
  const [durationType, setDurationType] = useState('fixed');
  const [selectedDuration, setSelectedDuration] = useState(15);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø¥Ø¶Ø§ÙÙŠØ©
  const [selectedOpenTimeRental, setSelectedOpenTimeRental] = useState(null);
  const [editRental, setEditRental] = useState(null);
  const [editGame, setEditGame] = useState(null);
  const [cancelRental, setCancelRental] = useState(null);
  const [cancelAmount, setCancelAmount] = useState('');
  const [endRental, setEndRental] = useState(null);
  const [endAmount, setEndAmount] = useState('');
  const [remainingAmount, setRemainingAmount] = useState(0);
  const [selectedNewGame, setSelectedNewGame] = useState(null);
  const [reRentalData, setReRentalData] = useState(null);
  const [reRentalGame, setReRentalGame] = useState(null);
  const [reRentalDuration, setReRentalDuration] = useState(60);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø´ÙŠÙØª
  const [currentShift, setCurrentShift] = useState(null);
  const [shiftStats, setShiftStats] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    activeRentals: 0
  });
  
  // Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª
  const [statistics, setStatistics] = useState({
    availableGames: 0,
    totalGames: 0,
    todayRevenue: 0,
    todayRentals: 0
  });
  
  // ØªÙ†Ø¨ÙŠÙ‡Ø§Øª Ø§Ù„ÙˆÙ‚Øª Ø§Ù„Ù…Ù†ØªÙ‡ÙŠ
  const [expiringAlerts, setExpiringAlerts] = useState([]);
  
  // Refs
  const timersRef = useRef({});
  const countdownTimersRef = useRef({});
  
  // ==================== CONSTANTS ====================
  const fixedDurations = [
    { value: 15, label: '15 Ø¯Ù‚ÙŠÙ‚Ø©', priceMultiplier: 1 },
    { value: 30, label: '30 Ø¯Ù‚ÙŠÙ‚Ø©', priceMultiplier: 2 },
    { value: 45, label: '45 Ø¯Ù‚ÙŠÙ‚Ø©', priceMultiplier: 3 },
    { value: 60, label: 'Ø³Ø§Ø¹Ø© ÙˆØ§Ø­Ø¯Ø©', priceMultiplier: 4 },
    { value: 120, label: 'Ø³Ø§Ø¹ØªÙŠÙ†', priceMultiplier: 8 },
    { value: 180, label: '3 Ø³Ø§Ø¹Ø§Øª', priceMultiplier: 12 },
    { value: 240, label: '4 Ø³Ø§Ø¹Ø§Øª', priceMultiplier: 16 },
    { value: 300, label: '5 Ø³Ø§Ø¹Ø§Øª', priceMultiplier: 20 },
    { value: 360, label: '6 Ø³Ø§Ø¹Ø§Øª', priceMultiplier: 24 }
  ];

  // ==================== ENHANCED SHIFT & RENTAL MANAGEMENT ====================

  // State Ø¬Ø¯ÙŠØ¯ Ù„Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„Ù…Ø­Ø³Ù†Ø©
  const [enhancedShiftStats, setEnhancedShiftStats] = useState({
    shift_id: null,
    total_revenue: 0,
    total_rentals: 0,
    active_rentals: 0,
    completed_rentals: 0,
    avg_rental_value: 0,
    peak_hours: [],
    popular_games: [],
    revenue_by_hour: [],
    unique_customers: 0
  });

  // Ø¯Ø§Ù„Ø© Ù„ØªØ­Ù…ÙŠÙ„ Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„Ø´ÙŠÙØª Ø§Ù„Ù…Ø­Ø³Ù†Ø©
  const loadEnhancedShiftStats = async () => {
    try {
      if (!currentShift?.id) return;
      
      const response = await api.get(`/shifts/${currentShift.id}/rentals`);
      
      if (response.success) {
        setEnhancedShiftStats({
          shift_id: currentShift.id,
          total_revenue: response.stats.total_revenue || 0,
          total_rentals: response.stats.total_rentals || 0,
          active_rentals: response.stats.active_rentals || 0,
          completed_rentals: response.stats.completed_rentals || 0,
          unique_customers: response.stats.unique_customers || 0,
          avg_rental_value: response.stats.total_rentals > 0 
            ? response.stats.total_revenue / response.stats.total_rentals 
            : 0
        });
      }
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„Ø´ÙŠÙØª:', error);
    }
  };

  // Ø¯Ø§Ù„Ø© Ù„ØªØ­Ø³ÙŠÙ† Ø¬Ù…ÙŠØ¹ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
  const enhanceRentalData = async () => {
    try {
      showNotification('info', 'ğŸ”„ Ø¬Ø§Ø±ÙŠ ØªØ­Ø³ÙŠÙ† Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª ÙˆØ±Ø¨Ø·Ù‡Ø§ Ø¨Ø§Ù„Ø´ÙŠÙØª...');
      
      const response = await api.put('/rentals/enhance-shift-data');
      
      if (response.success) {
        showNotification('success', 'âœ… ØªÙ… ØªØ­Ø³ÙŠÙ† Ø¬Ù…ÙŠØ¹ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª ÙˆØ±Ø¨Ø·Ù‡Ø§ Ø¨Ø§Ù„Ø´ÙŠÙØª ÙˆØ§Ù„ØªØ§Ø±ÙŠØ®');
        await loadData();
        await loadEnhancedShiftStats();
      }
    } catch (error) {
      showNotification('error', 'âŒ ÙØ´Ù„ ÙÙŠ ØªØ­Ø³ÙŠÙ† Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª');
    }
  };

const handleEnhancedStartRental = async () => {
  if (!currentShift) {
    showNotification('error', 'ÙŠØ¬Ø¨ Ø¨Ø¯Ø¡ Ø´ÙŠÙØª Ø£ÙˆÙ„Ø§Ù‹');
    return;
  }
  
  if (!customerName.trim() || !customerPhone.trim()) {
    showNotification('error', 'Ø§Ù„Ø±Ø¬Ø§Ø¡ Ø¥Ø¯Ø®Ø§Ù„ Ø§Ø³Ù… Ø§Ù„Ø¹Ù…ÙŠÙ„ ÙˆØ±Ù‚Ù… Ø§Ù„Ù‡Ø§ØªÙ');
    return;
  }
  
  if (selectedGames.length === 0) {
    showNotification('error', 'Ø§Ù„Ø±Ø¬Ø§Ø¡ Ø§Ø®ØªÙŠØ§Ø± Ù„Ø¹Ø¨Ø© ÙˆØ§Ø­Ø¯Ø© Ø¹Ù„Ù‰ Ø§Ù„Ø£Ù‚Ù„');
    return;
  }
  
  try {
    showNotification('info', 'ğŸš€ Ø¬Ø§Ø±ÙŠ Ø¨Ø¯Ø¡ ØªØ£Ø¬ÙŠØ± Ù…Ø­Ø³Ù†...');
    
    const today = new Date().toISOString().split('T')[0];
    
    // Ø¥Ù†Ø´Ø§Ø¡ ØªØ£Ø¬ÙŠØ±Ø§Øª Ù…ØªØ¹Ø¯Ø¯Ø© Ù…Ø±ØªØ¨Ø·Ø© Ø¨Ø§Ù„Ø´ÙŠÙØª
    const rentalPromises = selectedGames.map(async (game) => {
      const totalAmount = calculateGamePrice(game, selectedDuration);
      const paidAmount = durationType === 'open' ? 0 : totalAmount;
      
      const rentalData = {
        game_id: game.id,
        customer_name: customerName.trim(),
        customer_phone: customerPhone.trim(),
        shift_id: currentShift.id,
        shift_date: today, // âš¡ Ø±Ø¨Ø· Ø¨ØªØ§Ø±ÙŠØ® Ø§Ù„ÙŠÙˆÙ…
        duration_minutes: selectedDuration,
        total_amount: totalAmount,
        paid_amount: paidAmount,
        payment_method: paymentMethod,
        payment_status: durationType === 'open' ? 'Ø¹Ù†Ø¯ Ø§Ù„Ø¥Ù†Ù‡Ø§Ø¡' : 'Ù…Ø¯ÙÙˆØ¹',
        notes: customerNotes || '',
        branch_id: user.branch_id,
        employee_id: user.id,
        employee_name: user.name,
        created_at: new Date().toISOString()
      };
      
      return await api.post('/rentals/enhanced', rentalData);
    });
    
    const results = await Promise.allSettled(rentalPromises);
    
    // ØªØ­Ø¯ÙŠØ« Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„Ø´ÙŠÙØª
    const newRevenue = selectedGames.reduce((sum, game) => {
      return sum + calculateGamePrice(game, selectedDuration);
    }, 0);
    
    setEnhancedShiftStats(prev => ({
      ...prev,
      total_revenue: prev.total_revenue + newRevenue,
      total_rentals: prev.total_rentals + selectedGames.length,
      active_rentals: prev.active_rentals + selectedGames.length
    }));
    
    showNotification('success', `âœ… ØªÙ… Ø¨Ø¯Ø¡ ${selectedGames.length} ØªØ£Ø¬ÙŠØ± Ø¨Ù†Ø¬Ø§Ø­`);
    
    // Ø¥Ø¹Ø§Ø¯Ø© ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª
    await Promise.all([
      loadGames(),
      loadActiveRentals(),
      loadCompletedRentals(),
      loadEnhancedShiftStats()
    ]);
    
    // Ø¥ØºÙ„Ø§Ù‚ ÙˆØ¥Ø¹Ø§Ø¯Ø© ØªØ¹ÙŠÙŠÙ†
    setShowRentalModal(false);
    resetForm();
    
  } catch (error) {
    console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ Ø¨Ø¯Ø¡ ØªØ£Ø¬ÙŠØ± Ù…Ø­Ø³Ù†:', error);
    showNotification('error', 'Ø­Ø¯Ø« Ø®Ø·Ø£ ÙÙŠ Ø¨Ø¯Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ±');
  }
};

  // ==================== HELPER FUNCTIONS ====================
  const startCountdownTimer = (rentalId, startTimeStr, durationMinutes) => {
    if (countdownTimersRef.current[rentalId]) {
