import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import authService from '../../services/authService';
import './GamesManagement.css';
import api from '../../services/api';

const GamesManagement = () => {
  const { user } = useAuth();
  const [allGames, setAllGames] = useState([]); // ุฌููุน ุงูุฃูุนุงุจ ูู ุฌููุน ุงููุฑูุน
  const [currentGames, setCurrentGames] = useState([]); // ุงูุฃูุนุงุจ ุงููุนุฑูุถุฉ ุญุงููุงู
  const [branches, setBranches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [loadingBranch, setLoadingBranch] = useState(false);
  const [error, setError] = useState('');
  const [selectedBranch, setSelectedBranch] = useState('all');
  const [branchStats, setBranchStats] = useState({});

  useEffect(() => {
    loadGamesData(); 
  }, []);

  const loadGamesData = async () => {
    try {
      setLoading(true);
      setError('');

      // ุฌูุจ ุงูุฃูุนุงุจ
      const gamesResponse = await authService.getGames();
      
      console.log('๐ฎ ุงุณุชุฌุงุจุฉ getGames:', {
        success: gamesResponse.success,
        dataLength: gamesResponse.data?.length,
        message: gamesResponse.message
      });
      
      if (gamesResponse.success) {
        const gamesData = gamesResponse.data || [];
        console.log('โ ุนุฏุฏ ุงูุฃูุนุงุจ ุงููุญููุฉ:', gamesData.length);
        
        setAllGames(gamesData);
        setCurrentGames(gamesData);
        
        // ุฌูุจ ุงููุฑูุน
        const branchesResponse = await authService.getBranches();
        if (branchesResponse.success) {
          const branchesData = branchesResponse.data || [];
          console.log('๐ฌ ุนุฏุฏ ุงููุฑูุน ุงููุญููุฉ:', branchesData.length);
          setBranches(branchesData);
          
          // โ **ุญุณุงุจ ุงูุฅุญุตุงุฆูุงุช ุงููุจุณุทุฉ (ุนุฏุฏ ุงูุฃูุนุงุจ ููุท)**
          const stats = {};
          
          // ุชููุฆุฉ ุงูุฅุญุตุงุฆูุงุช ููู ูุฑุน
          branchesData.forEach(branch => {
            stats[branch.id] = {
              branch_id: branch.id,
              branch_name: branch.name,
              total_games: 0,
              gameCounts: {}
            };
          });
          
          // ุญุณุงุจ ุงูุฃูุนุงุจ ููู ูุฑุน
          gamesData.forEach(game => {
            const branchId = game.branch_id;
            if (stats[branchId]) {
              stats[branchId].total_games++;
              
              // ุฒูุงุฏุฉ ุนุฏุฏ ูู ููุน ูู ุงูุฃูุนุงุจ
