import { useState, useEffect } from 'react';
import authService from '../../services/authService';
import './AdminPages.css';

const BranchesManagement = () => {
  const [branches, setBranches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showGamesModal, setShowGamesModal] = useState(false);
  const [showShiftsModal, setShowShiftsModal] = useState(false);
  const [selectedBranch, setSelectedBranch] = useState(null);
  const [availableGames, setAvailableGames] = useState([]);
  const [branchGames, setBranchGames] = useState([]);
  const [branchShifts, setBranchShifts] = useState([]);
  const [loadingGames, setLoadingGames] = useState(false);
  const [loadingShifts, setLoadingShifts] = useState(false);
  const [gamesError, setGamesError] = useState('');
  const [shiftsError, setShiftsError] = useState('');
  const [currentShift, setCurrentShift] = useState(null);

  
  const [newBranch, setNewBranch] = useState({
    name: '',
    location: '',
    city: 'Ø§Ù„Ù‚Ø§Ù‡Ø±Ø©',
    contact_phone: '',
    contact_email: '',
    opening_time: '10:00:00',
    closing_time: '22:00:00'
  });

  const [editBranch, setEditBranch] = useState({
    id: '',
    name: '',
    location: '',
    city: '',
    contact_phone: '',
    contact_email: '',
    opening_time: '',
    closing_time: '',
    is_active: true
  });

  const defaultGamesList = [
    { id: 1, name: 'Ø¯Ø±ÙŠÙØª ÙƒØ§Ø±', category: 'Ø³ÙŠØ§Ø±Ø§Øª', suggestedPricePer15min: 50, image: 'drift-car.jpg' },
    { id: 2, name: 'Ù‡Ø§ÙØ± Ø¨ÙˆØ±Ø¯', category: 'ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', suggestedPricePer15min: 50, image: 'hoverboard.jpg' },
    { id: 3, name: 'Ù…ÙˆØªÙˆØ³ÙŠÙƒÙ„ ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠ', category: 'Ø¯Ø±Ø§Ø¬Ø§Øª Ù†Ø§Ø±ÙŠØ©', suggestedPricePer15min: 50, image: 'electric-bike.jpg' },
    { id: 4, name: 'Ø¹Ø±Ø¨Ø© ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', category: 'Ø³ÙŠØ§Ø±Ø§Øª', suggestedPricePer15min: 50, image: 'electric-car.jpg' },
    { id: 5, name: 'Ø³ÙƒÙˆØªØ± ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠ', category: 'ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', suggestedPricePer15min: 70, image: 'electric-scooter.jpg' },
    { id: 6, name: 'Ù‡Ø§Ø±Ù„ÙŠ', category: 'Ø¯Ø±Ø§Ø¬Ø§Øª Ù†Ø§Ø±ÙŠØ©', suggestedPricePer15min: 60, image: 'harley.jpg' },
    { id: 7, name: 'Ø³ÙŠØ¬ÙˆØ§ÙŠ', category: 'ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', suggestedPricePer15min: 60, image: 'segway.jpg' }
  ];

  const loadBranches = async () => {
    try {
      setLoading(true);
      setError('');
      
      const response = await authService.getBranches();
      
      if (response.success) {
        setBranches(response.data);
      } else {
        setError(response.message || 'ÙØ´Ù„ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ÙØ±ÙˆØ¹');
      }
    } catch (error) {
      setError('ØªØ¹Ø°Ø± Ø§Ù„Ø§ØªØµØ§Ù„ Ø¨Ø§Ù„Ø®Ø§Ø¯Ù…');
    } finally {
      setLoading(false);
    }
  };


// âš¡ Ø¯Ø§Ù„Ø© Ù„ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø´ÙŠÙØª Ø§Ù„Ø­Ø§Ù„ÙŠ
  const loadCurrentShift = async () => {
    try {
      const response = await authService.getActiveShift(); // ØªØ£ÙƒØ¯ Ù…Ù† Ø£Ù† authService ÙŠØ­ÙˆÙŠ Ù‡Ø°Ù‡ Ø§Ù„Ø¯Ø§Ù„Ø©
      if (response.success && response.data) {
        setCurrentShift(response.data);
      }
    } catch (error) {
      console.error('Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø´ÙŠÙØª Ø§Ù„Ø­Ø§Ù„ÙŠ:', error);
    }
  };


// Ø§Ø³ØªØ¯Ø¹Ø§Ø¡ loadCurrentShift ÙÙŠ useEffect
useEffect(() => {
  loadBranches();
  loadCurrentShift(); // âš¡ Ø¥Ø¶Ø§ÙØ© Ù‡Ø°Ø§ Ø§Ù„Ø³Ø·Ø±
}, []);

  const loadBranchGames = async (branchId) => {
    try {
      setLoadingGames(true);
      setGamesError('');
      
      const response = await authService.getBranchGames(branchId);
      
      if (response.success) {
        const availableGames = response.data.filter(game => game.status === 'Ù…ØªØ§Ø­');
        setBranchGames(availableGames);
      } else {
        const errorMessage = response.message || 'ÙØ´Ù„ ÙÙŠ Ø¬Ù„Ø¨ Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„ÙØ±Ø¹';
        setGamesError(errorMessage);
        setBranchGames([]);
      }
    } catch (error) {
      setGamesError('ØªØ¹Ø°Ø± Ø§Ù„Ø§ØªØµØ§Ù„ Ø¨Ø§Ù„Ø®Ø§Ø¯Ù…: ' + error.message);
      setBranchGames([]);
    } finally {
      setLoadingGames(false);
    }
  };

  const loadBranchShifts = async (branchId) => {
    try {
      setLoadingShifts(true);
      setShiftsError('');
      
      const response = await authService.get(`/api/shifts?branch_id=${branchId}`);
      
      if (response.data && response.data.success) {
        setBranchShifts(response.data.data || []);
      } else {
        const errorMessage = response.data?.message || 'ÙØ´Ù„ ÙÙŠ Ø¬Ù„Ø¨ Ø´ÙŠÙØªØ§Øª Ø§Ù„ÙØ±Ø¹';
        setShiftsError(errorMessage);
        setBranchShifts([]);
      }
    } catch (error) {
      setShiftsError('ØªØ¹Ø°Ø± Ø§Ù„Ø§ØªØµØ§Ù„ Ø¨Ø§Ù„Ø®Ø§Ø¯Ù…: ' + error.message);
      setBranchShifts([]);
    } finally {
      setLoadingShifts(false);
    }
  };

  useEffect(() => {
    loadBranches();
  }, []);

