// frontend/src/pages/Login.jsx - تحديث
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();
  const navigate = useNavigate();

// src/pages/Login.jsx
const handleSubmit = async (e) => {
  e.preventDefault();
  setLoading(true);
  
  const result = await login(email, password);
  
  if (result.success) {
    // جلب بيانات إضافية للمستخدم
    try {
      const userResponse = await api.get('/auth/me');
      if (userResponse.success) {
        const fullUserData = {
          ...userResponse.user,
          branch_name: userResponse.user.branch?.name || 'غير محدد'
        };
        
        // تحديث بيانات المستخدم في context
        // ... قد تحتاج لإضافة دالة updateUser في AuthContext
      }
    } catch (error) {
      console.warn('⚠️ لم يتم تحميل بيانات إضافية:', error);
    }
    
    // التوجيه بناءً على الدور
    const role = result.user?.role || user?.role;
    if (role === 'admin') {
      navigate('/admin/dashboard');
    } else {
      navigate('/employee/dashboard');
    }
  } else {
    setError(result.message || 'فشل تسجيل الدخول');
  }
  setLoading(false);
};

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <img src="/images/logol3bty.jpg" alt="L3BTY Logo" className="logo" />
          <h1>نظام تأجير الألعاب</h1>
          <p>L3BTY Rental System</p>
        </div>

        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="email">البريد الإلكتروني</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="example@l3bty.com"
              required
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">كلمة المرور</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="********"
              required
              disabled={loading}
            />
          </div>

          {error && (
            <div className="error-message">
              ⚠️ {error}
            </div>
          )}

          <button 
            type="submit" 
            className="login-btn"
            disabled={loading}
          >
            {loading ? 'جاري تسجيل الدخول...' : 'تسجيل الدخول'}
          </button>
        </form>

        <div className="login-footer">
          <p>© 2024 L3BTY Rental System. جميع الحقوق محفوظة.</p>
          <p className="hint">
            للدخول كمسؤول: admin@l3bty.com / 123456
          </p>
          <p className="hint">
            للدخول كموظف: employee@l3bty.com / 123456
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
