import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const Rentals = () => {
  const { user } = useAuth();
  const [branch, setBranch] = useState(null);
  const [loading, setLoading] = useState(true);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ ÙˆØ§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
  const [games, setGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  
  // Ù†Ø§ÙØ°Ø© Ø¨Ø¯Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ±
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [selectedGames, setSelectedGames] = useState([]);
  const [quantities, setQuantities] = useState({});
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù†Ù…ÙˆØ°Ø¬
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');
  const [durationType, setDurationType] = useState('fixed');
  const [selectedDuration, setSelectedDuration] = useState(15);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [showAddMoreGames, setShowAddMoreGames] = useState(false);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø´ÙŠÙØª
  const [currentShift, setCurrentShift] = useState(null);
  
  // Ù†Ø§ÙØ°Ø§Øª Ø¥Ø¶Ø§ÙÙŠØ©
  const [showEditModal, setShowEditModal] = useState(false);
  const [editRental, setEditRental] = useState(null);
  const [editGame, setEditGame] = useState(null);
  const [showCancelModal, setShowCancelModal] = useState(false);
  const [cancelRental, setCancelRental] = useState(null);
  const [cancelAmount, setCancelAmount] = useState('');
  const [showEndModal, setShowEndModal] = useState(false);
  const [endRental, setEndRental] = useState(null);
  const [endAmount, setEndAmount] = useState('');
  const [remainingAmount, setRemainingAmount] = useState(0);
  
  // Refs Ù„Ù„Ù…Ø¤Ù‚ØªØ§Øª
  const timersRef = useRef({});
  const countdownTimersRef = useRef({});

  // Ù…Ø¯Ø© Ø§Ù„ØªØ£Ø¬ÙŠØ± Ø§Ù„Ø«Ø§Ø¨ØªØ©
  const fixedDurations = [
    { value: 15, label: '15 Ø¯Ù‚ÙŠÙ‚Ø©' },
    { value: 30, label: '30 Ø¯Ù‚ÙŠÙ‚Ø©' },
    { value: 45, label: '45 Ø¯Ù‚ÙŠÙ‚Ø©' },
    { value: 60, label: 'Ø³Ø§Ø¹Ø© ÙˆØ§Ø­Ø¯Ø©' },
    { value: 120, label: 'Ø³Ø§Ø¹ØªÙŠÙ†' },
    { value: 180, label: '3 Ø³Ø§Ø¹Ø§Øª' },
    { value: 240, label: '4 Ø³Ø§Ø¹Ø§Øª' },
    { value: 300, label: '5 Ø³Ø§Ø¹Ø§Øª' },
    { value: 360, label: '6 Ø³Ø§Ø¹Ø§Øª' }
  ];

  // ==================== USE EFFECTS ====================

  useEffect(() => {
    if (user) {
      loadShiftData();
      loadData();
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª ÙƒÙ„ 30 Ø«Ø§Ù†ÙŠØ©
      const interval = setInterval(() => {
        loadData();
      }, 30000);
      
      return () => {
        clearInterval(interval);
        Object.values(timersRef.current).forEach(timer => clearInterval(timer));
        Object.values(countdownTimersRef.current).forEach(timer => clearInterval(timer));
      };
    }
  }, [user]);

  // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¹Ø¯Ø§Ø¯Ø§Øª Ø§Ù„ØªÙ†Ø§Ø²Ù„ÙŠØ©
  useEffect(() => {
    // ØªÙ†Ø¸ÙŠÙ Ø§Ù„Ù…Ø¤Ù‚ØªØ§Øª Ø§Ù„Ø³Ø§Ø¨Ù‚Ø©
    Object.values(countdownTimersRef.current).forEach(timer => clearInterval(timer));
    countdownTimersRef.current = {};
    
    // Ø¥Ù†Ø´Ø§Ø¡ Ù…Ø¤Ù‚ØªØ§Øª Ø¬Ø¯ÙŠØ¯Ø©
    activeRentals.forEach(rental => {
      if (rental.duration_minutes > 0 && rental.status === 'Ù†Ø´Ø·') {
        startCountdownTimer(rental.id, rental.start_time, rental.duration_minutes);
      }
    });
    
    return () => {
      Object.values(countdownTimersRef.current).forEach(timer => clearInterval(timer));
    };
  }, [activeRentals]);

  useEffect(() => {
    const checkExpiredRentals = () => {
      const now = new Date();
      const expiredRentals = activeRentals.filter(rental => {
        if (rental.duration_minutes > 0 && rental.status === 'Ù†Ø´Ø·') {
          const startTime = new Date(rental.start_time);
          const endTime = new Date(startTime.getTime() + rental.duration_minutes * 60000);
          return now >= endTime;
        }
        return false;
      });

      if (expiredRentals.length > 0) {
        // Ù†Ù‚Ù„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…Ù†ØªÙ‡ÙŠØ© ØªÙ„Ù‚Ø§Ø¦ÙŠØ§Ù‹ Ø¥Ù„Ù‰ Ø§Ù„Ù…ÙƒØªÙ…Ù„Ø©
        expiredRentals.forEach(rental => {
          handleAutoCompleteRental(rental);
        });
      }
    };

    const interval = setInterval(checkExpiredRentals, 60000); // Ø§Ù„ØªØ­Ù‚Ù‚ ÙƒÙ„ Ø¯Ù‚ÙŠÙ‚Ø©
    return () => clearInterval(interval);
  }, [activeRentals]);


  const loadShiftData = async () => {
    try {
      if (!user?.branch_id) return;
      
      const response = await api.getActiveShift();
      if (response.success) {
        setCurrentShift(response.data);
      }
    } catch (error) {
      console.error('Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø´ÙŠÙØª:', error);
    }
  };

  const startShift = async () => {
    try {
      const response = await api.startShift();
      if (response.success) {
        setCurrentShift(response.data);
        showNotification('success', 'ØªÙ… Ø¨Ø¯Ø¡ Ø§Ù„Ø´ÙŠÙØª Ø¨Ù†Ø¬Ø§Ø­');
        loadData();
      }
    } catch (error) {
      showNotification('error', 'ÙØ´Ù„ Ø¨Ø¯Ø¡ Ø§Ù„Ø´ÙŠÙØª');
    }
  };

  const loadData = async () => {
    try {
      if (!user?.branch_id) return;
      
      // ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„ÙØ±Ø¹
      const branchResponse = await api.get(`/branches/${user.branch_id}`);
      if (branchResponse.success) {
        setBranch(branchResponse.data);
      }
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨
      await loadGames();
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø©
      await loadActiveRentals();
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…ÙƒØªÙ…Ù„Ø©
      await loadCompletedRentals();
      
    } catch (error) {
      console.error('Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª:', error);
      showNotification('error', 'ÙØ´Ù„ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª');
    } finally {
      setLoading(false);
    }
  };

  const loadGames = async () => {
    try {
      if (!user?.branch_id) return;
      
      const gamesResponse = await api.get('/games', {
        params: { 
          branch_id: user.branch_id,
          status: 'all'
        }
      });
      
      if (gamesResponse.success) {
        const uniqueGames = [];
        const gameNames = new Set();
        
        gamesResponse.data.forEach(game => {
          if (!gameNames.has(game.name)) {
            gameNames.add(game.name);
            uniqueGames.push({
              ...game,
              id: parseInt(game.id),
              name: game.name || 'ØºÙŠØ± Ù…Ø¹Ø±ÙˆÙ',
              status: game.status || 'Ù…ØªØ§Ø­',
              price_per_15min: game.price_per_15min || 100,
              category: game.category || 'ØºÙŠØ± Ù…Ø­Ø¯Ø¯',
              image_url: game.image_url || 'default-game.jpg'
            });
          }
        });
        
        setGames(uniqueGames);
      } else {
        setGames([]);
      }
    } catch (error) {
      console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', error);
      setGames([]);
    }
  };

  const loadActiveRentals = async () => {
    try {
      if (!user?.branch_id) return;
      
      const response = await api.get('/rentals', {
        params: { 
          branch_id: user.branch_id,
          status: 'Ù†Ø´Ø·',
          limit: 50,
          include_customer: true,
          include_game: true
        }
      });
      
      if (response.success) {
        const rentalsWithTimers = response.data.map(rental => {
          if (rental.duration_minutes > 0) {
            const startTime = new Date(rental.start_time);
            const endTime = new Date(startTime.getTime() + rental.duration_minutes * 60000);
            const now = new Date();
            const timeLeftMs = endTime - now;
            
            let timeLeft = 'Ø§Ù†ØªÙ‡Ù‰';
            let isExpired = false;
            
            if (timeLeftMs > 0) {
              const minutesLeft = Math.floor(timeLeftMs / 60000);
