import React, { useState, useEffect, useCallback } from 'react';
import authService from '../../services/authService';
import './AdminPages.css';
import api from '../../services/api';

// ŸÜÿ≠ÿ∞ŸÅ ÿßÿ≥ÿ™Ÿäÿ±ÿßÿØ ÿßŸÑÿµŸàÿ± ÿ®ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ require ŸÑÿ£ŸÜŸáÿß ŸÅŸä ŸÖÿ¨ŸÑÿØ public
// ÿ®ÿØŸÑÿßŸã ŸÖŸÜ ÿ∞ŸÑŸÉ ŸÜÿ≥ÿ™ÿÆÿØŸÖ ŸÖÿ≥ÿßÿ±ÿßÿ™ ŸÜÿ≥ÿ®Ÿäÿ©

const BranchesManagement = () => {
  // üîπ States ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ©
  const [branches, setBranches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showGamesModal, setShowGamesModal] = useState(false);
  const [showShiftsModal, setShowShiftsModal] = useState(false);
  const [selectedBranch, setSelectedBranch] = useState(null);
  const [availableGames, setAvailableGames] = useState([]);
  const [branchGames, setBranchGames] = useState([]);
  const [branchShifts, setBranchShifts] = useState([]);
  const [loadingGames, setLoadingGames] = useState(false);
  const [loadingShifts, setLoadingShifts] = useState(false);
  const [gamesError, setGamesError] = useState('');
  const [shiftsError, setShiftsError] = useState('');
  const [currentShift, setCurrentShift] = useState(null);
  const [diagnosticInfo, setDiagnosticInfo] = useState(null);

  // üîπ ÿÆÿ±Ÿäÿ∑ÿ© ŸÖÿ≥ÿßÿ±ÿßÿ™ ÿßŸÑÿµŸàÿ± ÿßŸÑÿµÿ≠Ÿäÿ≠ÿ© - ŸÖÿ≥ÿßÿ±ÿßÿ™ ŸÖÿ®ÿßÿ¥ÿ±ÿ© ŸÖŸÜ public folder
  const GAME_IMAGE_MAP = {
    // ÿÆÿ±Ÿäÿ∑ÿ© ÿßŸÑÿ£ÿ≥ŸÖÿßÿ° ÿßŸÑÿ•ŸÜÿ¨ŸÑŸäÿ≤Ÿäÿ© ŸÖÿπ ŸÖÿ≥ÿßÿ±ÿßÿ™ ÿßŸÑÿµŸàÿ±
    'Driftcar': '/images/Driftcar.jpg',
