    'Hoverboard': '/images/Hoverboard.jpg',
    'ŸáŸàŸÅÿ± ÿ®Ÿàÿ±ÿØ': '/images/Hoverboard.jpg',
    'ŸáŸàŸÅÿ±ÿ®Ÿàÿ±ÿØ': '/images/Hoverboard.jpg',
    'ŸáÿßŸÅÿ± ÿ®Ÿàÿ±ÿØ': '/images/Hoverboard.jpg',
    
    'Scooter': '/images/Scooter.jpg',
    'ÿ≥ŸÉŸàÿ™ÿ±': '/images/Scooter.jpg',
    'ÿßÿ≥ŸÉŸàÿ™ÿ±': '/images/Scooter.jpg',
    'ÿ≥ŸÉŸàÿ™ÿ± ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿä': '/images/Scooter.jpg',
    
    'Electric car': '/images/Car.jpg',
    'Electric Car': '/images/Car.jpg',
    'ŸÉÿßÿ± ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿä': '/images/Car.jpg',
    'ÿ≥Ÿäÿßÿ±ÿ© ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿäÿ©': '/images/Car.jpg',
    'ÿπÿ±ÿ®ÿ© ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿäÿ©': '/images/Car.jpg',
    
    'Electric motor': '/images/Motor.jpg',
    'Electric Motor': '/images/Motor.jpg',
    'ŸÖŸàÿ™Ÿàÿ≥ŸäŸÉŸÑ ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿä': '/images/Motor.jpg',
    'ŸÖŸàÿ™Ÿàÿ≥ŸäŸÉŸÑ': '/images/Motor.jpg',
    'ÿØÿ±ÿßÿ¨ÿ© ŸÜÿßÿ±Ÿäÿ©': '/images/Motor.jpg',
    
    'Segway': '/images/Segway.jpg',
    'ÿ≥ÿ¨ŸàŸä': '/images/Segway.jpg',
