      return DEFAULT_IMAGES.defaultGameImg;
    }

    // ÿ•ŸÜÿ¥ÿßÿ° ŸÜÿ≥ÿÆÿ© ÿ¢ŸÖŸÜÿ© ŸÖŸÜ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
    const safeGame = {
      name: game?.name ? String(game.name).trim() : '',
      image_url: game?.image_url || '',
      display_image: game?.display_image || '',
      thumbnail_url: game?.thumbnail_url || '',
      image_status: game?.image_status || 'completed',
      category: game?.category || ''
    };

    console.log('üîç ÿßŸÑÿ®ÿ≠ÿ´ ÿπŸÜ ÿµŸàÿ±ÿ© ŸÑŸÑÿπÿ®ÿ©:', safeGame.name);

    // 1. ÿßŸÑÿ®ÿ≠ÿ´ ŸÅŸä ÿÆÿ±Ÿäÿ∑ÿ© GAME_IMAGE_MAP ÿ®ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑÿßÿ≥ŸÖ
    if (safeGame.name) {
      const gameName = safeGame.name.trim();
      
      // ÿßŸÑÿ®ÿ≠ÿ´ ÿßŸÑÿØŸÇŸäŸÇ ÿ£ŸàŸÑÿßŸã
      if (GAME_IMAGE_MAP[gameName]) {
        console.log(`‚úÖ ÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ÿµŸàÿ±ÿ© ŸÖÿ∑ÿßÿ®ŸÇÿ© ŸÅŸä ÿßŸÑÿÆÿ±Ÿäÿ∑ÿ© ŸÑŸÄ: ${gameName}`);
        return GAME_IMAGE_MAP[gameName];
      }
      
      // ÿßŸÑÿ®ÿ≠ÿ´ ÿßŸÑÿ¨ÿ≤ÿ¶Ÿä ŸÅŸä ÿ£ÿ≥ŸÖÿßÿ° ÿßŸÑÿ£ŸÑÿπÿßÿ®
      for (const [key, imagePath] of Object.entries(GAME_IMAGE_MAP)) {
        const keyLower = key.toLowerCase();
        const gameNameLower = gameName.toLowerCase();
        
        if (gameNameLower.includes(keyLower) || keyLower.includes(gameNameLower)) {
          console.log(`‚úÖ ÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ŸÖÿ∑ÿßÿ®ŸÇÿ© ÿ¨ÿ≤ÿ¶Ÿäÿ© ŸÅŸä ÿßŸÑÿÆÿ±Ÿäÿ∑ÿ©: ${key} -> ${gameName}`);
          return imagePath;
        }
      }
    }

    // 2. ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ display_image ŸÖŸÜ ÿßŸÑÿÆÿßÿØŸÖ
    if (safeGame.display_image && safeGame.display_image !== '/images/default-game.jpg') {
      console.log('‚úÖ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ display_image:', safeGame.display_image);
      
      if (safeGame.display_image.startsWith('/images/')) {
        return safeGame.display_image;
      }
      
      if (safeGame.display_image.startsWith('http')) {
        return safeGame.display_image;
      }
      
      return `/images/${safeGame.display_image}`;
    }

    // 3. ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ image_url ÿ•ÿ∞ÿß ŸÉÿßŸÜÿ™ ŸÖŸÉÿ™ŸÖŸÑÿ©
    if (safeGame.image_url && safeGame.image_status === 'completed') {
      console.log('‚úÖ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ image_url:', safeGame.image_url);
      
      let cleanUrl = String(safeGame.image_url).trim();
      
      if (cleanUrl.startsWith('/images/')) {
        return cleanUrl;
      }
      
      if (!cleanUrl.startsWith('http') && !cleanUrl.startsWith('/')) {
        cleanUrl = `/images/${cleanUrl}`;
      }
      
      return cleanUrl;
    }

    // 4. ÿ•ÿ∞ÿß ŸÉÿßŸÜÿ™ ÿßŸÑÿµŸàÿ±ÿ© ŸÇŸäÿØ ÿßŸÑŸÖÿπÿßŸÑÿ¨ÿ©
    if (safeGame.image_status === 'processing') {
      console.log('‚è≥ ÿßŸÑÿµŸàÿ±ÿ© ŸÇŸäÿØ ÿßŸÑŸÖÿπÿßŸÑÿ¨ÿ©');
      return DEFAULT_IMAGES.defaultGameLoading || DEFAULT_IMAGES.defaultGameImg;
    }

    // 5. ÿßŸÑÿ®ÿ≠ÿ´ ÿ®ÿßŸÑÿ™ÿµŸÜŸäŸÅ
    if (safeGame.category) {
      const categoryMap = {
        'ÿ≥Ÿäÿßÿ±ÿßÿ™': DEFAULT_IMAGES.driftCarImg,
        'ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿäÿ©': DEFAULT_IMAGES.hoverboardImg,
        'ÿØÿ±ÿßÿ¨ÿßÿ™ ŸÜÿßÿ±Ÿäÿ©': DEFAULT_IMAGES.harleyImg,
        'ÿ≥ŸÉŸàÿ™ÿ±': DEFAULT_IMAGES.scooterImg,
        'Ÿáÿßÿ±ŸÑŸä': DEFAULT_IMAGES.harleyImg,
        'ÿØÿ±ÿßÿ¨ÿ© ŸÜÿßÿ±Ÿäÿ©': DEFAULT_IMAGES.harleyImg,
        'ŸÖÿ≠ÿ±ŸÉ ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿä': DEFAULT_IMAGES.motorImg,
        'ÿ≥Ÿäÿßÿ±ÿ©': DEFAULT_IMAGES.carImg,
        'ÿ™ÿ±ŸÅŸäŸá': DEFAULT_IMAGES.trampolineImg
      };
      
      if (categoryMap[safeGame.category]) {
        console.log(`‚úÖ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿµŸàÿ±ÿ© ÿßŸÑÿ™ÿµŸÜŸäŸÅ: ${safeGame.category}`);
        return categoryMap[safeGame.category];
      }
    }

    // 6. ÿßŸÑÿµŸàÿ±ÿ© ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ©
    console.log('üîÑ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑÿµŸàÿ±ÿ© ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ©');
    return DEFAULT_IMAGES.defaultGameImg;
  }, [GAME_IMAGE_MAP]);

