const autoAddGameWithUniqueName = async (game) => {
  if (!selectedBranch) return;
  
  const baseName = game.name;
  const price = game.suggestedPricePer15min || 50;
  let gameName = baseName;
  let attempts = 1;
  
  // ÿßŸÑÿ®ÿ≠ÿ´ ÿπŸÜ ÿßÿ≥ŸÖ ŸÅÿ±ŸäÿØ
  try {
    const gamesResponse = await fetch(`http://localhost:5000/api/branches/${selectedBranch.id}/games`);
    if (gamesResponse.ok) {
      const gamesData = await gamesResponse.json();
      const existingNames = gamesData.data?.map(g => g.name.toLowerCase()) || [];
      
