      const hours = Math.floor(minutes / 60);
      const remaining = minutes % 60;
      return `${hours} ÿ≥ÿßÿπÿ© ${remaining > 0 ? remaining + ' ÿØŸÇŸäŸÇÿ©' : ''}`;
