// ==================== دوال المساعدة ====================
const validatePhone = phone => {
  const phoneRegex = /^01[0-9]{9}$/;
  return phoneRegex.test(phone);
};
const formatCurrency = amount => {
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 2
  }).format(num);
};

// ==================== دوال المساعدة للصور ====================
const getGameImageUrl = game => {
  if (!game) return '/images/default-game.jpg';
  let imageUrl = game.image_url || game.external_image_url || game.thumbnail_url;
  if (!imageUrl) return '/images/default-game.jpg';
  if (imageUrl.startsWith('http://') || imageUrl.startsWith('https://')) {
    return imageUrl;
  }
  if (imageUrl.startsWith('/')) {
    return imageUrl;
  }
  return `/images/${imageUrl}`;
};
const handleImageError = e => {
  e.target.onerror = null;
  e.target.src = '/images/default-game.jpg';
};

