      }
      
      try {
        if (isEmployee) {
          await updateEmployeeStatistics();
        } else {
          await updateManagerStatistics();
        }
      } catch (statsError) {
        console.error('❌ خطأ في الإحصائيات:', statsError);
      }
      
      console.log('✅ تم تحميل البيانات بنجاح');
      
    } catch (error) {
      console.error('❌ خطأ فادح في تحميل البيانات:', error);
      showNotification('error', 'فشل في تحميل بعض البيانات، جاري العمل على الحل');
