      showNotification('error', 'حدث خطأ في تحميل الألعاب');
    }
  };

  const processGamesData = (data) => {
    let gamesData = [];
    
    if (Array.isArray(data)) {
      gamesData = data;
    } else if (data && Array.isArray(data.data)) {
      gamesData = data.data;
    } else if (data && typeof data === 'object') {
      gamesData = Object.values(data);
