      console.log('👑 جلب إحصائيات المدير');
      
      const response = await api.get('/dashboard/stats/manager');
      
      if (response.success && response.data) {
        const stats = response.data;
        
        const today = new Date();
        const todayStr = today.toISOString().split('T')[0];
        
        const newStats = {
          availableGames: stats.games?.available_games || 0,
          totalGames: stats.games?.total_games || 0,
          todayRevenue: stats.today?.today_revenue || 0,
          todayRentals: stats.today?.completed_rentals || 0,
