  };

  const updateStatistics = async (gamesData = games) => {
    try {
      console.log('💰 تحديث الإحصائيات...');
      
      const availableGamesCount = gamesData.filter(g => 
        g.status === 'متاح' && (g.is_active === 1 || g.is_active === true)
