        uniqueCustomers: statistics.uniqueCustomers || 0
      };
      
      console.log('📊 الإحصائيات المحدثة:', newStats);
      setStatistics(newStats);
      
    } catch (error) {
      console.error('❌ خطأ في تحديث الإحصائيات:', error);
      
      setStatistics(prev => ({
        ...prev,
        availableGames: gamesData.filter(g => g.status === 'متاح').length,
        totalGames: gamesData.length
      }));
    }
  };

  const startCountdownTimer = (rentalId, startTime, durationMinutes) => {
    console.log(`⏱️ بدء عداد للتأجير ${rentalId}`);
    
    if (durationMinutes <= 0) {
      console.log(`⚠️ تأجير ${rentalId}: وقت مفتوح بدون عداد`);
      return null;
    }
    
    if (countdownTimersRef.current[rentalId]) {
      clearInterval(countdownTimersRef.current[rentalId]);
    }
    
    const startTimeDate = startTime instanceof Date ? startTime : new Date(startTime);
    const endTime = new Date(startTimeDate.getTime() + durationMinutes * 60000);
    
    const timer = setInterval(() => {
      setActiveRentals(prev => prev.map(rental => {
        if (rental.id === rentalId) {
          const now = new Date();
          const timeLeftMs = endTime - now;
          
          if (timeLeftMs <= 0) {
            console.log(`🛑 تأجير ${rentalId} انتهى`);
            clearInterval(timer);
            delete countdownTimersRef.current[rentalId];
            
            return {
              ...rental,
              time_left: 'انتهى',
              remaining_seconds: 0,
              is_expired: true
            };
          }
          
          const totalSeconds = Math.floor(timeLeftMs / 1000);
          const hours = Math.floor(totalSeconds / 3600);
          const minutes = Math.floor((totalSeconds % 3600) / 60);
          const seconds = totalSeconds % 60;
          
          const timeLeftFormatted = hours > 0 
            ? `${hours}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`
            : `${minutes}:${seconds.toString().padStart(2, '0')}`;
          
          return {
            ...rental,
            time_left: timeLeftFormatted,
            remaining_seconds: totalSeconds,
            is_expired: false
          };
        }
        return rental;
      }));
    }, 1000);
    
    countdownTimersRef.current[rentalId] = timer;
    console.log(`✅ تم بدء العداد للتأجير ${rentalId}`);
    return timer;
  };

  // ==================== دالة جديدة: التحقق من التأجيرات المنتهية وتحويلها ====================
  const checkAndAutoCompleteRentals = async () => {
    try {
      const now = new Date();
      const expiredRentals = activeRentals.filter(rental => {
        if (rental.is_open_time || rental.duration_minutes === 0) {
          return false; // الوقت المفتوح لا ينتهي تلقائياً
        }
        
        const startTime = new Date(rental.start_time);
        const endTime = new Date(startTime.getTime() + rental.duration_minutes * 60000);
        return now > endTime && !rental.is_expired;
      });
      
      if (expiredRentals.length > 0) {
        console.log(`🔄 العثور على ${expiredRentals.length} تأجير منتهي للتحويل`);
        
        for (const rental of expiredRentals) {
          await handleAutoCompleteRental(rental);
        }
