    const timeInterval = setInterval(() => {
      setCurrentTime(new Date());
      updateTimeAgo();
    }, 1000);
    
    // ØªØ­Ø¯ÙŠØ« Ø§Ù„ÙˆÙ‚Øª Ø§Ù„Ù…Ù†Ù‚Ø¶ÙŠ
    const timeAgoInterval = setInterval(updateTimeAgo, 60000);
    
