          { id: 3, name: 'Ø¹Ø±Ø¨Ø© ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', category: 'Ø³ÙŠØ§Ø±Ø§Øª', price_per_hour: 200, status: 'Ù…Ø¤Ø¬Ø±', rental_count: 8, total_revenue: 3200, image_url: 'electric-car.jpg' },
          { id: 4, name: 'Ù‡Ø§ÙØ± Ø¨ÙˆØ±Ø¯', category: 'ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', price_per_hour: 80, status: 'Ù…ØªØ§Ø­', rental_count: 6, total_revenue: 2400, image_url: 'hoverboard.jpg' },
          { id: 5, name: 'Ø³ÙƒÙˆØªØ± ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠ', category: 'ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', price_per_hour: 70, status: 'Ù…ØªØ§Ø­', rental_count: 5, total_revenue: 2100, image_url: 'electric-scooter.jpg' }
        ];
        setGames(mockGames);
        calculateTopGames(mockGames);
      }
      
      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
      if (rentalsResponse.status === 'fulfilled' && rentalsResponse.value.success) {
        setRecentRentals(rentalsResponse.value.data || []);
        setStats(prev => ({ ...prev, activeRentals: rentalsResponse.value.data.length }));
      } else {
        const mockRentals = [
          { 
            id: 1, 
            rental_number: 'RENT-001',
            customer_name: 'Ø£Ø­Ù…Ø¯ Ù…Ø­Ù…Ø¯',
            customer_phone: '01012345678',
            game_name: 'Ø¯Ø±ÙŠÙØª ÙƒØ§Ø±',
            branch_name: 'Ø§Ù„ÙØ±Ø¹ Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠ',
            total_amount: 150,
            status: 'Ù†Ø´Ø·',
            start_time: new Date(Date.now() - 30 * 60000).toISOString(),
            duration_minutes: 30
          },
          { 
            id: 2, 
            rental_number: 'RENT-002',
            customer_name: 'Ù…Ø­Ù…Ø¯ Ø¹Ù„ÙŠ',
            customer_phone: '01123456789',
            game_name: 'Ù…ÙˆØªÙˆØ³ÙŠÙƒÙ„ ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠ',
            branch_name: 'ÙØ±Ø¹ Ø§Ù„Ù…Ø¹Ø§Ø¯ÙŠ',
            total_amount: 120,
            status: 'Ù†Ø´Ø·',
            start_time: new Date(Date.now() - 45 * 60000).toISOString(),
            duration_minutes: 45
          }
        ];
        setRecentRentals(mockRentals);
      }
      
      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù…ÙŠÙ†
      if (usersResponse.status === 'fulfilled' && usersResponse.value.success) {
        const usersData = usersResponse.value.data || [];
        setUsers(usersData);
        setStats(prev => ({ ...prev, totalUsers: usersData.length }));
        calculateTopCustomers(usersData);
      } else {
        calculateTopCustomers([]);
      }

      // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø¥ÙŠØ±Ø§Ø¯Ø§Øª Ø§Ù„Ø´Ù‡Ø±ÙŠØ©
      const currentDate = new Date();
      const mockMonthlyRevenue = [];
      for (let i = 5; i >= 0; i--) {
        const date = new Date(currentDate.getFullYear(), currentDate.getMonth() - i, 1);
        mockMonthlyRevenue.push({
