import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const Rentals = () => {
  // ==================== CONTEXT & STATES ====================
  const { user } = useAuth();
  
  // States Ø§Ù„Ø£Ø³Ø§Ø³ÙŠØ©
  const [branch, setBranch] = useState(null);
  const [loading, setLoading] = useState(true);
  const [loadingMessage, setLoadingMessage] = useState('Ø¬Ø§Ø±ÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª...');
  const [currentEgyptTime, setCurrentEgyptTime] = useState('');
  const [currentShift, setCurrentShift] = useState(null);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ ÙˆØ§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
  const [games, setGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  
  // Ù†Ø§ÙØ°Ø§Øª Ù…Ø®ØªÙ„ÙØ©
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showCancelModal, setShowCancelModal] = useState(false);
  const [showEndModal, setShowEndModal] = useState(false);
  const [showChangeGameModal, setShowChangeGameModal] = useState(false);
  const [showReRentalModal, setShowReRentalModal] = useState(false);
  const [showOpenTimeActions, setShowOpenTimeActions] = useState(false);
  const [showAddMoreGames, setShowAddMoreGames] = useState(false);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù†Ù…Ø§Ø°Ø¬
  const [selectedGames, setSelectedGames] = useState([]);
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');
  const [durationType, setDurationType] = useState('fixed');
  const [selectedDuration, setSelectedDuration] = useState(15);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [depositAmount, setDepositAmount] = useState('');
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø¥Ø¶Ø§ÙÙŠØ©
  const [selectedOpenTimeRental, setSelectedOpenTimeRental] = useState(null);
  const [editRental, setEditRental] = useState(null);
  const [editGame, setEditGame] = useState(null);
  const [cancelRental, setCancelRental] = useState(null);
  const [cancelAmount, setCancelAmount] = useState('');
  const [endRental, setEndRental] = useState(null);
  const [endAmount, setEndAmount] = useState('');
  const [remainingAmount, setRemainingAmount] = useState(0);
  const [selectedNewGame, setSelectedNewGame] = useState(null);
  const [reRentalData, setReRentalData] = useState(null);
  const [reRentalGame, setReRentalGame] = useState(null);
  const [reRentalDuration, setReRentalDuration] = useState(60);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø´ÙŠÙØª ÙˆØ§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª
  const [shiftStats, setShiftStats] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });
  
  const [statistics, setStatistics] = useState({
    availableGames: 0,
    totalGames: 0,
    totalRevenue: 0,
    totalRentals: 0
  });
  
  // ØªÙ†Ø¨ÙŠÙ‡Ø§Øª Ø§Ù„ÙˆÙ‚Øª Ø§Ù„Ù…Ù†ØªÙ‡ÙŠ
  const [expiringAlerts, setExpiringAlerts] = useState([]);
  
  // Refs
  const timersRef = useRef({});
  const countdownTimersRef = useRef({});
  
  // ==================== CONSTANTS ====================
  const fixedDurations = [
    { value: 15, label: '15 Ø¯Ù‚ÙŠÙ‚Ø©', priceMultiplier: 1 },
    { value: 30, label: '30 Ø¯Ù‚ÙŠÙ‚Ø©', priceMultiplier: 2 },
    { value: 45, label: '45 Ø¯Ù‚ÙŠÙ‚Ø©', priceMultiplier: 3 },
    { value: 60, label: 'Ø³Ø§Ø¹Ø© ÙˆØ§Ø­Ø¯Ø©', priceMultiplier: 4 },
    { value: 120, label: 'Ø³Ø§Ø¹ØªÙŠÙ†', priceMultiplier: 8 },
    { value: 180, label: '3 Ø³Ø§Ø¹Ø§Øª', priceMultiplier: 12 },
    { value: 240, label: '4 Ø³Ø§Ø¹Ø§Øª', priceMultiplier: 16 },
    { value: 300, label: '5 Ø³Ø§Ø¹Ø§Øª', priceMultiplier: 20 },
    { value: 360, label: '6 Ø³Ø§Ø¹Ø§Øª', priceMultiplier: 24 }
  ];

  // ==================== TIME FUNCTIONS ====================

  // ØªØ­ÙˆÙŠÙ„ Ø£ÙŠ ÙˆÙ‚Øª Ø¥Ù„Ù‰ ØªÙˆÙ‚ÙŠØª Ù…ØµØ±
  const convertToEgyptTime = (dateString = null) => {
    try {
      const date = dateString ? new Date(dateString) : new Date();
      const options = { 
        timeZone: 'Africa/Cairo',
        hour12: false,
        hour: '2-digit',
        minute: '2-digit'
      };
      
      const timeString = date.toLocaleTimeString('ar-EG', options);
      const [hours, minutes] = timeString.split(':');
      
      return {
        hours: hours.padStart(2, '0'),
        minutes: minutes.padStart(2, '0'),
        formatted: `${hours.padStart(2, '0')}:${minutes.padStart(2, '0')}`,
        date: date
      };
    } catch (error) {
      const date = dateString ? new Date(dateString) : new Date();
      return {
        hours: date.getHours().toString().padStart(2, '0'),
        minutes: date.getMinutes().toString().padStart(2, '0'),
        formatted: `${date.getHours().toString().padStart(2, '0')}:${date.getMinutes().toString().padStart(2, '0')}`,
        date: date
      };
    }
  };

  // Ø§Ù„ÙˆÙ‚Øª Ø§Ù„Ø­Ø§Ù„ÙŠ Ø¨ØªÙˆÙ‚ÙŠØª Ù…ØµØ±
  const getCurrentEgyptTimeHM = () => {
    const now = new Date();
    const options = { 
      timeZone: 'Africa/Cairo',
      hour12: false,
      hour: '2-digit',
      minute: '2-digit'
    };
    return now.toLocaleTimeString('ar-EG', options);
  };

  // ØªÙ†Ø³ÙŠÙ‚ Ø§Ù„ÙˆÙ‚Øª
  const formatEgyptTime = (dateString) => {
    if (!dateString) return '--:--';
    return convertToEgyptTime(dateString).formatted;
  };

  // ØªÙ†Ø³ÙŠÙ‚ Ø§Ù„ØªØ§Ø±ÙŠØ® ÙˆØ§Ù„ÙˆÙ‚Øª
  const formatEgyptDateTime = (dateString) => {
    try {
      if (!dateString) return '--/--/---- --:--';
      
      const date = new Date(dateString);
      const options = {
        timeZone: 'Africa/Cairo',
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit',
        hour12: false
      };
      
      const formatter = new Intl.DateTimeFormat('ar-EG', options);
      const parts = formatter.formatToParts(date);
      
      const year = parts.find(p => p.type === 'year')?.value;
      const month = parts.find(p => p.type === 'month')?.value;
      const day = parts.find(p => p.type === 'day')?.value;
      const hour = parts.find(p => p.type === 'hour')?.value;
      const minute = parts.find(p => p.type === 'minute')?.value;
      
      return `${day}/${month}/${year} ${hour}:${minute}`;
    } catch (error) {
      const date = new Date(dateString);
      return `${date.getDate().toString().padStart(2, '0')}/${(date.getMonth() + 1).toString().padStart(2, '0')}/${date.getFullYear()} ${date.getHours().toString().padStart(2, '0')}:${date.getMinutes().toString().padStart(2, '0')}`;
    }
  };

  // Ø­Ø³Ø§Ø¨ Ù…Ø¯Ø© Ø§Ù„Ø´ÙŠÙØª
  const calculateShiftDuration = (startTimeStr) => {
    if (!startTimeStr) return '00:00';
    
    try {
      const startDate = new Date(startTimeStr);
      const now = new Date();
      const diffMs = now.getTime() - startDate.getTime();
      
      if (diffMs < 0) return '00:00';
      
      const totalMinutes = Math.floor(diffMs / (1000 * 60));
      const hours = Math.floor(totalMinutes / 60);
      const minutes = totalMinutes % 60;
      
      return `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}`;
    } catch (error) {
      return '00:00';
    }
  };

  // Ø§Ù„Ø­ØµÙˆÙ„ Ø¹Ù„Ù‰ Ø§Ù„ØªØ§Ø±ÙŠØ® Ø§Ù„Ø­Ø§Ù„ÙŠ Ø¨ØªÙˆÙ‚ÙŠØª Ù…ØµØ±
  const getCurrentEgyptDate = () => {
    try {
      const now = new Date();
      const egyptTimeString = now.toLocaleString('ar-EG', { timeZone: 'Africa/Cairo' });
      return new Date(egyptTimeString);
    } catch (error) {
      return new Date();
    }
  };

  // Ø­Ø³Ø§Ø¨ Ø§Ù„ÙØ±Ù‚ Ø¨Ø§Ù„Ø¯Ù‚Ø§Ø¦Ù‚ Ø¨ÙŠÙ† ÙˆÙ‚ØªÙŠÙ†
  const calculateTimeDifferenceInMinutes = (startTimeStr, endTimeStr = null) => {
    try {
      const start = new Date(startTimeStr);
      const end = endTimeStr ? new Date(endTimeStr) : getCurrentEgyptDate();
      const diffMs = end.getTime() - start.getTime();
      return Math.floor(diffMs / (1000 * 60));
    } catch (error) {
      return 0;
    }
  };

  // ==================== USE EFFECTS ====================

  // ØªØ­Ø¯ÙŠØ« Ø§Ù„ÙˆÙ‚Øª Ø§Ù„Ø­Ø§Ù„ÙŠ
  useEffect(() => {
    const updateEgyptTime = () => {
      setCurrentEgyptTime(getCurrentEgyptTimeHM());
    };
    
    updateEgyptTime();
    const interval = setInterval(updateEgyptTime, 60000);
    
    return () => clearInterval(interval);
  }, []);

  // Ø§Ù„ØªØ£Ø«ÙŠØ± Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠ Ù„ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª
  useEffect(() => {
    if (user) {
      loadShiftData();
      loadData();
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª ÙƒÙ„ 30 Ø«Ø§Ù†ÙŠØ©
      const dataInterval = setInterval(() => {
        loadData();
      }, 30000);
      
      // ØªØ­Ø¯ÙŠØ« Ø­Ø§Ù„Ø© Ø§Ù„Ø´ÙŠÙØª ÙƒÙ„ Ø¯Ù‚ÙŠÙ‚Ø©
      const shiftTimerInterval = setInterval(() => {
        if (currentShift) {
          setCurrentShift(prev => prev ? { ...prev } : null);
        }
      }, 60000);
      
      // Ø§Ù„ØªØ­Ù‚Ù‚ Ù…Ù† Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…Ù†ØªÙ‡ÙŠØ© ÙƒÙ„ Ø¯Ù‚ÙŠÙ‚Ø©
      const checkExpiredInterval = setInterval(() => {
        checkAndAutoCompleteExpiredRentals();
      }, 60000);
      
      return () => {
        clearInterval(dataInterval);
        clearInterval(shiftTimerInterval);
        clearInterval(checkExpiredInterval);
        Object.values(timersRef.current).forEach(timer => clearInterval(timer));
        Object.values(countdownTimersRef.current).forEach(timer => clearInterval(timer));
      };
    }
  }, [user]);

  // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¹Ø¯Ø§Ø¯Ø§Øª Ø§Ù„ØªÙ†Ø§Ø²Ù„ÙŠØ©
  useEffect(() => {
    Object.values(countdownTimersRef.current).forEach(timer => clearInterval(timer));
    countdownTimersRef.current = {};
    
    activeRentals.forEach(rental => {
      if (rental.duration_minutes > 0 && rental.status === 'Ù†Ø´Ø·') {
        startCountdownTimer(rental.id, rental.start_time, rental.duration_minutes);
      }
    });
    
    return () => {
      Object.values(countdownTimersRef.current).forEach(timer => clearInterval(timer));
    };
  }, [activeRentals]);

  // Ø§Ù„ØªØ­Ù‚Ù‚ Ù…Ù† Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…Ù†ØªÙ‡ÙŠØ© ÙˆØ§Ù„Ø¥ÙƒÙ…Ø§Ù„ Ø§Ù„ØªÙ„Ù‚Ø§Ø¦ÙŠ
  useEffect(() => {
    const checkAndCompleteExpiredRentals = () => {
      const now = new Date();
      const alerts = [];
      
      activeRentals.forEach(rental => {
        if (rental.duration_minutes > 0 && rental.status === 'Ù†Ø´Ø·') {
          const startTime = new Date(rental.start_time);
          const endTime = new Date(startTime.getTime() + rental.duration_minutes * 60000);
          const timeLeftMs = endTime - now;
          
          if (timeLeftMs <= 0) {
            handleAutoCompleteRental(rental);
          } else if (timeLeftMs <= 300000) { // 5 Ø¯Ù‚Ø§Ø¦Ù‚
            alerts.push({
              rentalId: rental.id,
              rentalNumber: rental.rental_number,
              customerName: rental.customer_name,
              gameName: rental.game_name,
              timeLeft: Math.ceil(timeLeftMs / 60000)
            });
          }
        }
      });
      
      setExpiringAlerts(alerts);
    };
    
    const interval = setInterval(checkAndCompleteExpiredRentals, 60000);
    return () => clearInterval(interval);
  }, [activeRentals]);

  // Ø¥ØºÙ„Ø§Ù‚ Ø§Ù„Ù†ÙˆØ§ÙØ° Ø¹Ù†Ø¯ Ø§Ù„Ù†Ù‚Ø± Ø®Ø§Ø±Ø¬Ù‡Ø§
  useEffect(() => {
    const handleClickOutside = (event) => {
      const modals = [
        showRentalModal,
        showEditModal,
        showCancelModal,
        showEndModal,
        showChangeGameModal,
        showReRentalModal,
        showOpenTimeActions,
        showAddMoreGames
      ];
      
      if (modals.some(modal => modal)) {
        const modalElements = document.querySelectorAll('.modal-overlay, .modal');
        let clickedInsideModal = false;
        
        modalElements.forEach(element => {
          if (element.contains(event.target)) {
            clickedInsideModal = true;
          }
        });
        
        if (!clickedInsideModal) {
          setShowRentalModal(false);
          setShowEditModal(false);
          setShowCancelModal(false);
          setShowEndModal(false);
          setShowChangeGameModal(false);
          setShowReRentalModal(false);
          setShowOpenTimeActions(false);
          setShowAddMoreGames(false);
          
          setEditRental(null);
          setCancelRental(null);
          setEndRental(null);
          setSelectedOpenTimeRental(null);
          setReRentalData(null);
          resetForm();
        }
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    document.addEventListener('touchstart', handleClickOutside);
    
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('touchstart', handleClickOutside);
    };
  }, [showRentalModal, showEditModal, showCancelModal, showEndModal, showChangeGameModal, showReRentalModal, showOpenTimeActions, showAddMoreGames]);

  // ==================== API FUNCTIONS ====================

  // ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø´ÙŠÙØª
  const loadShiftData = async () => {
    try {
      if (!user?.branch_id) return;
      
      const response = await api.getActiveShift();
      
      if (response.success && response.data) {
        setCurrentShift(response.data);
        
        // ØªØ­Ù…ÙŠÙ„ ØªØ£Ø¬ÙŠØ±Ø§Øª Ù‡Ø°Ø§ Ø§Ù„Ø´ÙŠÙØª
        const shiftId = response.data.id;
        const shiftResponse = await api.get('/rentals', {
          params: {
            shift_id: shiftId,
            status: 'all'
          }
        });
        
        if (shiftResponse.success) {
          // ÙØµÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø© Ø¹Ù† Ø§Ù„Ù…ÙƒØªÙ…Ù„Ø©
