// frontend/src/context/RentalContext.jsx
import React, { createContext, useState, useContext } from 'react';

const RentalContext = createContext();

export const useRental = () => useContext(RentalContext);

export const RentalProvider = ({ children }) => {
  const [activeRentals, setActiveRentals] = useState([]);
  const [availableGames, setAvailableGames] = useState([]);

  const addRental = (rental) => {
    setActiveRentals(prev => [...prev, rental]);
  };

  const removeRental = (rentalId) => {
    setActiveRentals(prev => prev.filter(rental => rental.id !== rentalId));
  };

  const updateRental = (updatedRental) => {
    setActiveRentals(prev => 
      prev.map(rental => 
        rental.id === updatedRental.id ? updatedRental : rental
      )
    );
  };

  const loadGames = (games) => {
    setAvailableGames(games);
  };

  return (
    <RentalContext.Provider value={{
      activeRentals,
      availableGames,
      addRental,
      removeRental,
      updateRental,
      loadGames
    }}>
      {children}
    </RentalContext.Provider>
  );
};
