      // ÿßÿ≥ÿ™ÿÆÿØŸÖ api.login ŸÖÿ®ÿßÿ¥ÿ±ÿ© ÿ®ÿØŸÑÿßŸã ŸÖŸÜ axios.post
      const response = await api.login(email, password);
      
      if (response.success && response.data?.token && response.data?.user) {
        // ÿ≠ŸÅÿ∏ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÅŸä localStorage
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('user', JSON.stringify(response.data.user));
        
        // ÿ™ÿ≠ÿØŸäÿ´ header ÿßŸÑŸÄ axios
        api.setToken(response.data.token);
        
        return {
          success: true,
          data: response.data,
          message: 'ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿ®ŸÜÿ¨ÿßÿ≠'
        };
      } else {
        return {
          success: false,
          message: response.message || 'ŸÅÿ¥ŸÑ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ'
        };
      }
    } catch (error) {
      authService.logError('Login', error);
      
      return {
        success: false,
        message: error.message || 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ'
      };
    }
  },

  logout: () => {
    api.logout();
    return {
      success: true,
      message: 'ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨ ÿ®ŸÜÿ¨ÿßÿ≠'
    };
  },

  setupToken: () => {
    const token = authService.getToken();
    if (token) {
      api.setToken(token);
    }
    return token;
  },

  getCurrentUser: () => {
    try {
      const userStr = localStorage.getItem('user');
      return userStr ? JSON.parse(userStr) : null;
    } catch (error) {
      return null;
    }
  },

  getToken: () => {
    const token = localStorage.getItem('token');
    return token ? token.trim() : null;
  },

  isAuthenticated: () => {
    const token = authService.getToken();
    const user = authService.getCurrentUser();
    return !!(token && user);
  },

  // ==================== BRANCH TIMING ====================
  getCurrentBranchTiming: async () => {
    try {
      // ÿßÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑÿØÿßŸÑÿ© ÿßŸÑÿ¨ÿØŸäÿØÿ© ŸÖŸÜ api.js
      const response = await api.getCurrentBranchTiming();
      return response;
    } catch (error) {
      authService.logError('Get Current Branch Timing', error);
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿ™ÿ≠ŸÖŸäŸÑ ÿ™ŸàŸÇŸäÿ™ ÿßŸÑŸÅÿ±ÿπ',
        data: null
      };
    }
  },

  // ==================== SHIFT MANAGEMENT ====================
  getActiveShift: async () => {
    try {
      // ÿßÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑÿØÿßŸÑÿ© ÿßŸÑÿ¨ÿØŸäÿØÿ© ŸÖŸÜ api.js
      const response = await api.getActiveShift();
      return response;
    } catch (error) {
      authService.logError('Get Active Shift', error);
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿ¨ŸÑÿ® ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑŸÜÿ¥ÿ∑',
        data: null
      };
    }
