  startShift: async () => {
    try {
      const response = await api.startShift();
      return response;
    } catch (error) {
      authService.logError('Start Shift', error);
      return {
        success: false,
        message: error.message || 'ÿ™ÿπÿ∞ÿ± ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™'
      };
