      return response;
    } catch (error) {
      authService.logError('End Shift', error);
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™'
      };
