      console.log('üì§ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:', fullRentalData);
