      authService.logError('Create Rental', error);
      return {
        success: false,
        message: error.message || 'ÿ™ÿπÿ∞ÿ± ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±'
