  // ==================== CALCULATION FUNCTIONS ====================
  calculateRentalPrice: (game, durationMinutes, quantity = 1) => {
    if (!game) return 0;
    
    let price = 0;
    
    if (durationMinutes >= 60) {
      price = (game.price_per_hour || 0) * (durationMinutes / 60);
    } else {
      const quarters = Math.ceil(durationMinutes / 15);
      price = (game.price_per_15min || 0) * quarters;
    }
    
    return price * quantity;
  },

