import axios from 'axios';

// โ ุชุตุญูุญ URL ุงูุฎุงุฏู
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

console.log('๐ API Base URL:', API_BASE_URL);

const api = axios.create({
  baseURL: API_BASE_URL, // ุงูุขู: http://localhost:5000/api
  timeout: 30000,
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json',
    'Accept-Language': 'ar'
  }
});

// ==================== Interceptors ====================
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    
    // โ ุชุญุณูู: ุฅุตูุงุญ ุชููุงุฆู ูุฌููุน URLs ุงูุชู ุชุจุฏุฃ ุจู /api/
    if (config.url && config.url.startsWith('/api/')) {
      console.warn('โ๏ธ ุชุญุฐูุฑ: URL ูุจุฏุฃ ุจู /api/ ูุฑุชูู:', config.url);
      config.url = config.url.replace(/^\/api/, '');
      console.log('โ ุชู ุฅุตูุงุญ URL ุฅูู:', config.url);
    }
    
    // โ ุฅุตูุงุญ ุฎุงุต ูุจุนุถ ุงูููุงุท ุงููุนุฑููุฉ
    const urlFixMap = {
      '/api/rentals/process-completed': '/rentals/process-completed',
      '/api/rentals/check-fixed-time': '/rentals/check-fixed-time',
      '/api/rentals/expired': '/rentals/expired'
    };
    
    if (urlFixMap[config.url]) {
      console.log(`๐ ุชุตุญูุญ URL: ${config.url} -> ${urlFixMap[config.url]}`);
      config.url = urlFixMap[config.url];
    }
    
    // ุชุณุฌูู ุงูุทูุจุงุช (ูููู ุชุนุทููู ูู ุงูุฅูุชุงุฌ)
    if (process.env.NODE_ENV === 'development') {
      console.log('๐ API Request:', {
        method: config.method?.toUpperCase(),
        url: config.url,
        fullUrl: `${config.baseURL}${config.url}`
      });
    }
    
    return config;
  },
  (error) => {
    console.error('โ Request Interceptor Error:', error);
    return Promise.reject(error);
  }
);

api.interceptors.response.use(
  (response) => {
    // ุชุณุฌูู ุงูุงุณุชุฌุงุจุงุช (ูููู ุชุนุทููู ูู ุงูุฅูุชุงุฌ)
    if (process.env.NODE_ENV === 'development') {
      console.log('โ API Response:', {
        url: response.config.url,
        status: response.status,
        success: true
      });
    }
    
    // ุฅุฐุง ูุงูุช ุงูุงุณุชุฌุงุจุฉ ุชุญุชูู ุนูู data ูุจุงุดุฑุฉ
    if (response.data && typeof response.data === 'object') {
      return response.data;
    }
    
    return {
      success: true,
