// 16. ุฏูุงู ุฅุถุงููุฉ ูููุญุฉ ุงูุชุญูู
api.getDashboardAllStats = async () => {
  try {
    const today = new Date().toISOString().split('T')[0];
    
    const [branchesStats, todayStats] = await Promise.allSettled([
      api.getBranchesStatsEnhanced(),
      api.getTodayStatsEnhanced()
    ]);

    const result = {
      success: true,
      data: {}
    };

    if (branchesStats.status === 'fulfilled' && branchesStats.value.success) {
      result.data.branchRevenues = branchesStats.value.data.branchRevenues || [];
      result.data.completedRentalsByBranch = branchesStats.value.data.completedRentalsByBranch || [];
      result.data.totalBranches = branchesStats.value.data.totalBranches || 0;
    } else {
      result.data.branchRevenues = [];
      result.data.completedRentalsByBranch = [];
      result.data.totalBranches = 0;
    }

    if (todayStats.status === 'fulfilled' && todayStats.value.success) {
      result.data.todayRevenue = todayStats.value.data.revenue || 0;
      result.data.todayRentals = todayStats.value.data.rentals || 0;
    } else {
      result.data.todayRevenue = 0;
      result.data.todayRentals = 0;
    }

    result.data.generatedAt = new Date().toISOString();
    result.data.today = today;

    return result;
  } catch (error) {
    console.error('โ ุฎุทุฃ ูู ุฌูุจ ุฅุญุตุงุฆูุงุช ููุญุฉ ุงูุชุญูู:', error);
    return {
      success: false,
      message: 'ุชุนุฐุฑ ุชุญููู ุฅุญุตุงุฆูุงุช ููุญุฉ ุงูุชุญูู',
      data: {
        branchRevenues: [],
        completedRentalsByBranch: [],
        todayRevenue: 0,
        todayRentals: 0,
        generatedAt: new Date().toISOString(),
        today: new Date().toISOString().split('T')[0]
      }
    };
  }
};

api.getBranchesStatsEnhanced = async () => {
  try {
    const today = new Date().toISOString().split('T')[0];
    const branchesResponse = await api.getBranches();
    
    if (!branchesResponse.success || !Array.isArray(branchesResponse.data)) {
      return {
        success: true,
        data: {
          branchRevenues: [],
          completedRentalsByBranch: [],
          totalBranches: 0
        }
      };
    }

    const branches = branchesResponse.data;
    const branchStats = [];
    
    for (const branch of branches.slice(0, 5)) {
      try {
        const rentalsResponse = await api.get('/rentals', {
          params: {
            branch_id: branch.id,
            status: 'ููุชูู',
            start_date: today,
            end_date: today,
            limit: 50
          }
        });

        let revenue = 0;
        let completedCount = 0;

        if (rentalsResponse.success && Array.isArray(rentalsResponse.data)) {
          revenue = rentalsResponse.data.reduce((sum, rental) => sum + (rental.total_amount || 0), 0);
          completedCount = rentalsResponse.data.length;
        }

        branchStats.push({
          branchId: branch.id,
          branchName: branch.name || `ูุฑุน ${branch.id}`,
          revenue: revenue,
          completedCount: completedCount
        });
      } catch (error) {
        continue;
      }
    }

    return {
      success: true,
      data: {
        branchRevenues: branchStats.map(stat => ({
          branchId: stat.branchId,
          branchName: stat.branchName,
          revenue: stat.revenue
        })),
        completedRentalsByBranch: branchStats.map(stat => ({
          branchId: stat.branchId,
          branchName: stat.branchName,
          count: stat.completedCount
        })),
        totalBranches: branchStats.length
      }
    };
  } catch (error) {
    return {
      success: false,
      data: {
        branchRevenues: [],
        completedRentalsByBranch: [],
        totalBranches: 0
      }
    };
  }
};

api.getTodayStatsEnhanced = async () => {
  try {
    const today = new Date().toISOString().split('T')[0];
    
    const statsResponse = await api.get('/rentals', {
      params: {
        status: 'ููุชูู',
        start_date: today,
        end_date: today
      }
    });

    if (statsResponse.success && Array.isArray(statsResponse.data)) {
      const revenue = statsResponse.data.reduce((sum, rental) => sum + (rental.total_amount || 0), 0);
      const rentals = statsResponse.data.length;
      
      return {
        success: true,
        data: {
          revenue: revenue,
          rentals: rentals,
          date: today
        }
      };
    }
    
    return {
      success: true,
      data: {
        revenue: 0,
        rentals: 0,
        date: today
      }
    };
  } catch (error) {
    return {
      success: false,
      data: {
        revenue: 0,
        rentals: 0,
        date: new Date().toISOString().split('T')[0]
      }
    };
  }
};

