// frontend/src/App.js
import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom'; // إزالة BrowserRouter من هنا
import Login from './pages/Login';
import AdminDashboard from './pages/admin/Dashboard';
import EmployeeDashboard from './pages/employee/Dashboard';

function App() {
  // دالة للتحقق من التوثيق
  const isAuthenticated = () => {
    const token = localStorage.getItem('token');
    const user = localStorage.getItem('user');
    return !!(token && user);
  };

  // دالة للحصول على دور المستخدم
  const getUserRole = () => {
    const userStr = localStorage.getItem('user');
    if (userStr) {
      try {
        const user = JSON.parse(userStr);
        return user.role;
      } catch (e) {
        return null;
      }
    }
    return null;
  };

  return (
    // إزالة <BrowserRouter> من هنا
    <Routes>
      {/* صفحة تسجيل الدخول */}
      <Route path="/login" element={<Login />} />
      
      {/* لوحة المدير */}
      <Route path="/admin" element={
        isAuthenticated() && ['admin', 'branch_manager'].includes(getUserRole()) 
          ? <AdminDashboard />
          : <Navigate to="/login" replace />
      } />
      
      {/* لوحة الموظف */}
      <Route path="/employee" element={
        isAuthenticated() && getUserRole() === 'employee'
          ? <EmployeeDashboard />
          : <Navigate to="/login" replace />
      } />
      
      {/* الصفحة الرئيسية */}
      <Route path="/" element={
        isAuthenticated()
          ? (getUserRole() === 'employee'
              ? <Navigate to="/employee" replace />
              : <Navigate to="/admin" replace />)
          : <Navigate to="/login" replace />
      } />
      
      {/* جميع المسارات الأخرى */}
      <Route path="*" element={<Navigate to="/" replace />} />
    </Routes>
  );
}

export default App;
