import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const Rentals = () => {
  const { user } = useAuth();
  const [branch, setBranch] = useState(null);
  const [loading, setLoading] = useState(true);
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸàÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™
  const [games, setGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  
  // ŸÜÿßŸÅÿ∞ÿ© ÿ®ÿØÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [selectedGames, setSelectedGames] = useState([]);
  const [quantities, setQuantities] = useState({});
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÜŸÖŸàÿ∞ÿ¨
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');
  const [durationType, setDurationType] = useState('fixed');
const [selectedDuration, setSelectedDuration] = useState(15);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [showAddMoreGames, setShowAddMoreGames] = useState(false);
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™
  const [currentShift, setCurrentShift] = useState(null);
  
  // ŸÜÿßŸÅÿ∞ÿßÿ™ ÿ•ÿ∂ÿßŸÅŸäÿ©
  const [showEditModal, setShowEditModal] = useState(false);
  const [editRental, setEditRental] = useState(null);
  const [editGame, setEditGame] = useState(null);
  const [showCancelModal, setShowCancelModal] = useState(false);
  const [cancelRental, setCancelRental] = useState(null);
  const [cancelAmount, setCancelAmount] = useState('');
  const [showEndModal, setShowEndModal] = useState(false);
  const [endRental, setEndRental] = useState(null);
  const [endAmount, setEndAmount] = useState('');
  
  // Refs ŸÑŸÑŸÖÿ§ŸÇÿ™ÿßÿ™
  const timersRef = useRef({});
  const countdownTimersRef = useRef({});

  // ŸÖÿØÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑÿ´ÿßÿ®ÿ™ÿ©
  const fixedDurations = [
    { value: 15, label: '15 ÿØŸÇŸäŸÇÿ©' },
    { value: 30, label: '30 ÿØŸÇŸäŸÇÿ©' },
    { value: 45, label: '45 ÿØŸÇŸäŸÇÿ©' },
    { value: 60, label: 'ÿ≥ÿßÿπÿ© Ÿàÿßÿ≠ÿØÿ©' },
    { value: 120, label: 'ÿ≥ÿßÿπÿ™ŸäŸÜ' },
    { value: 180, label: '3 ÿ≥ÿßÿπÿßÿ™' },
    { value: 240, label: '4 ÿ≥ÿßÿπÿßÿ™' },
    { value: 300, label: '5 ÿ≥ÿßÿπÿßÿ™' },
    { value: 360, label: '6 ÿ≥ÿßÿπÿßÿ™' }
  ];

  // ==================== USE EFFECTS ====================

  useEffect(() => {
    if (user) {
      loadShiftData();
      loadData();
      
      // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÉŸÑ 30 ÿ´ÿßŸÜŸäÿ©
      const interval = setInterval(() => {
        loadData();
      }, 30000);
      
      return () => {
        clearInterval(interval);
        Object.values(timersRef.current).forEach(timer => clearInterval(timer));
        Object.values(countdownTimersRef.current).forEach(timer => clearInterval(timer));
      };
    }
  }, [user]);

  // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿπÿØÿßÿØÿßÿ™ ÿßŸÑÿ™ŸÜÿßÿ≤ŸÑŸäÿ©
  useEffect(() => {
    // ÿ™ŸÜÿ∏ŸäŸÅ ÿßŸÑŸÖÿ§ŸÇÿ™ÿßÿ™ ÿßŸÑÿ≥ÿßÿ®ŸÇÿ©
    Object.values(countdownTimersRef.current).forEach(timer => clearInterval(timer));
    countdownTimersRef.current = {};
    
    // ÿ•ŸÜÿ¥ÿßÿ° ŸÖÿ§ŸÇÿ™ÿßÿ™ ÿ¨ÿØŸäÿØÿ©
    activeRentals.forEach(rental => {
      if (rental.duration_minutes > 0 && rental.status === 'ŸÜÿ¥ÿ∑') {
        startCountdownTimer(rental.id, rental.start_time, rental.duration_minutes);
      }
    });
    
    return () => {
      Object.values(countdownTimersRef.current).forEach(timer => clearInterval(timer));
    };
  }, [activeRentals]);

  // ŸÖÿ±ÿßŸÇÿ®ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ© ŸÑŸÜŸÇŸÑ ÿßŸÑŸÖŸÜÿ™ŸáŸäÿ© ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã
  useEffect(() => {
    const checkExpiredRentals = () => {
      const now = new Date();
      const expiredRentals = activeRentals.filter(rental => {
        if (rental.duration_minutes > 0 && rental.status === 'ŸÜÿ¥ÿ∑') {
          const startTime = new Date(rental.start_time);
          const endTime = new Date(startTime.getTime() + rental.duration_minutes * 60000);
          return now >= endTime;
        }
        return false;
      });

      if (expiredRentals.length > 0) {
        // ŸÜŸÇŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÜÿ™ŸáŸäÿ© ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã ÿ•ŸÑŸâ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©
        expiredRentals.forEach(rental => {
          handleAutoCompleteRental(rental);
        });
      }
    };

    const interval = setInterval(checkExpiredRentals, 60000); // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÉŸÑ ÿØŸÇŸäŸÇÿ©
    return () => clearInterval(interval);
  }, [activeRentals]);

  // ==================== FUNCTIONS ====================

  const loadShiftData = async () => {
    try {
      if (!user?.branch_id) return;
      
      const response = await api.getActiveShift();
      if (response.success) {
        setCurrentShift(response.data);
      }
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™:', error);
    }
  };

  const startShift = async () => {
    try {
      const response = await api.startShift();
      if (response.success) {
        setCurrentShift(response.data);
        showNotification('success', 'ÿ™ŸÖ ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ ÿ®ŸÜÿ¨ÿßÿ≠');
        loadData();
      }
    } catch (error) {
      showNotification('error', 'ŸÅÿ¥ŸÑ ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™');
    }
  };

  const loadData = async () => {
    try {
      if (!user?.branch_id) return;
      
      // ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÅÿ±ÿπ
      const branchResponse = await api.get(`/branches/${user.branch_id}`);
      if (branchResponse.success) {
        setBranch(branchResponse.data);
      }
      
      // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®
      await loadGames();
      
      // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
      await loadActiveRentals();
      
      // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©
      await loadCompletedRentals();
      
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™:', error);
      showNotification('error', 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™');
    } finally {
      setLoading(false);
    }
  };

const loadGames = async () => {
  try {
    if (!user?.branch_id) return;
    
    const gamesResponse = await api.get('/games', {
      params: { 
        branch_id: user.branch_id,
        status: 'all'
      }
    });
    
    if (gamesResponse.success) {
      // ‚ö° ÿ•ÿµŸÑÿßÿ≠: ÿ™ÿµŸÅŸäÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖŸÉÿ±ÿ±ÿ© ÿ®ŸÜÿßÿ°Ÿã ÿπŸÑŸâ ÿßŸÑÿßÿ≥ŸÖ
      const uniqueGames = [];
      const gameNames = new Set();
      
      gamesResponse.data.forEach(game => {
        if (!gameNames.has(game.name)) {
          gameNames.add(game.name);
          uniqueGames.push({
            ...game,
            id: parseInt(game.id),
            name: game.name || 'ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ',
            status: game.status || 'ŸÖÿ™ÿßÿ≠',
            price_per_15min: game.price_per_15min || 100,
            category: game.category || 'ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ',
            image_url: game.image_url || 'default-game.jpg'
          });
        }
      });
      
      console.log('‚úÖ ÿ™ŸÖ ÿ™ÿ≠ŸÖŸäŸÑ ÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÅÿ±ÿπ:', uniqueGames.length);
      console.log('üìä ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®:', uniqueGames);
      setGames(uniqueGames);
    } else {
      console.error('‚ùå ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®:', gamesResponse.message);
      setGames([]);
    }
  } catch (error) {
    console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®:', error);
    setGames([]);
  }
};

  const loadActiveRentals = async () => {
    try {
      if (!user?.branch_id) return;
      
      const response = await api.get('/rentals', {
        params: { 
          branch_id: user.branch_id,
          status: 'ŸÜÿ¥ÿ∑',
          limit: 50
        }
      });
      
      if (response.success) {
        const rentalsWithTimers = response.data.map(rental => {
          if (rental.duration_minutes > 0) {
            const startTime = new Date(rental.start_time);
            const endTime = new Date(startTime.getTime() + rental.duration_minutes * 60000);
            const now = new Date();
            const timeLeftMs = endTime - now;
            
            let timeLeft = 'ÿßŸÜÿ™ŸáŸâ';
            let isExpired = false;
            
            if (timeLeftMs > 0) {
              const minutesLeft = Math.floor(timeLeftMs / 60000);
              const hours = Math.floor(minutesLeft / 60);
              const minutes = minutesLeft % 60;
              timeLeft = hours > 0 ? `${hours}:${minutes.toString().padStart(2, '0')}` : `${minutes} ÿØŸÇŸäŸÇÿ©`;
            } else {
              timeLeft = 'ÿßŸÜÿ™ŸáŸâ';
              isExpired = true;
            }
            
            return {
              ...rental,
              time_left: timeLeft,
              is_expired: isExpired
            };
          }
          return rental;
        });
        
        setActiveRentals(rentalsWithTimers);
      }
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©:', error);
      setActiveRentals([]);
    }
  };

  const loadCompletedRentals = async () => {
    try {
      if (!user?.branch_id) return;
      
      const response = await api.get('/rentals', {
        params: { 
          branch_id: user.branch_id,
          status: 'ŸÖŸÉÿ™ŸÖŸÑ',
          limit: 50,
          order_by: 'end_time',
          order: 'desc'
        }
      });
      
      if (response.success) {
        setCompletedRentals(response.data || []);
      }
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©:', error);
      setCompletedRentals([]);
    }
  };

  const handleAutoCompleteRental = async (rental) => {
    try {
      const response = await api.put(`/rentals/${rental.id}/complete`, {
        final_amount: rental.total_amount,
        payment_status: 'ŸÖÿØŸÅŸàÿπ',
        auto_complete: true
      });
      
      if (response.success) {
        // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑŸÇŸàÿßÿ¶ŸÖ
        await Promise.all([
          loadActiveRentals(),
          loadCompletedRentals(),
          loadGames()
        ]);
        
        showNotification('info', `ÿ™ŸÖ ŸÜŸÇŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± #${rental.rental_number} ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã ÿ•ŸÑŸâ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©`);
      }
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÉŸÖÿßŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã:', error);
    }
  };

  const startCountdownTimer = (rentalId, startTimeStr, durationMinutes) => {
    if (countdownTimersRef.current[rentalId]) {
      clearInterval(countdownTimersRef.current[rentalId]);
    }
    
    const timer = setInterval(() => {
      setActiveRentals(prev => {
        return prev.map(rental => {
          if (rental.id === rentalId) {
            const startTime = new Date(startTimeStr);
            const endTime = new Date(startTime.getTime() + durationMinutes * 60000);
            const now = new Date();
            const timeLeftMs = endTime - now;
            
            let timeLeft = 'ÿßŸÜÿ™ŸáŸâ';
            let isExpired = false;
            
            if (timeLeftMs > 0) {
              const minutesLeft = Math.floor(timeLeftMs / 60000);
              const hours = Math.floor(minutesLeft / 60);
              const minutes = minutesLeft % 60;
              timeLeft = hours > 0 ? `${hours}ÿ≥ ${minutes}ÿØ` : `${minutes} ÿØŸÇŸäŸÇÿ©`;
            } else {
              timeLeft = 'ÿßŸÜÿ™ŸáŸâ';
              isExpired = true;
              clearInterval(timer);
            }
            
            return {
              ...rental,
              time_left: timeLeft,
              is_expired: isExpired
            };
          }
          return rental;
        });
      });
    }, 60000); // ÿ™ÿ≠ÿØŸäÿ´ ŸÉŸÑ ÿØŸÇŸäŸÇÿ©
    
    countdownTimersRef.current[rentalId] = timer;
  };

const ensureCustomerExists = async (customerName, customerPhone, notes = '') => {
  try {
    console.log('üîç ŸÖÿ≠ÿßŸàŸÑÿ© ÿ•ŸÜÿ¥ÿßÿ°/ÿßŸÑÿ®ÿ≠ÿ´ ÿπŸÜ ÿπŸÖŸäŸÑ:', { customerName, customerPhone });
    
    const response = await api.post('/customers/quick', {
      name: customerName,
      phone: customerPhone,
      notes: notes
    });
    
    console.log('‚úÖ ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿπŸÖŸäŸÑ:', response);
    
    if (response.success) {
      return response.data.id;
    } else {
      console.warn('‚ö†Ô∏è ŸÅÿ¥ŸÑ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿπŸÖŸäŸÑÿå ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ID ÿßŸÅÿ™ÿ±ÿßÿ∂Ÿä');
      return 1; // ID ÿßŸÅÿ™ÿ±ÿßÿ∂Ÿä
    }
  } catch (error) {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿπŸÖŸäŸÑ:', error);
    return 1; // ID ÿßŸÅÿ™ÿ±ÿßÿ∂Ÿä
  }
};

const handleGameClick = (game) => {
  if (!currentShift) {
    showNotification('error', 'Ÿäÿ¨ÿ® ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ£ŸàŸÑÿßŸã ŸÇÿ®ŸÑ ÿ®ÿØÿ° ÿ£Ÿä ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™');
    return;
  }
  
  const isAlreadySelected = selectedGames.some(g => g.id === game.id);
  
  if (!isAlreadySelected) {
    setSelectedGames([...selectedGames, game]);
    setQuantities({
      ...quantities,
      [game.id]: 1
    });
  } else {
    setQuantities({
      ...quantities,
      [game.id]: (quantities[game.id] || 1) + 1
    });
  }
  
  if (!showRentalModal) {
    setShowRentalModal(true);
  }
  
  showNotification('success', `ÿ™ŸÖÿ™ ÿ•ÿ∂ÿßŸÅÿ© ${game.name} ÿ•ŸÑŸâ ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±`);
};

const handleAddGameFromModal = (game) => {
  const isAlreadySelected = selectedGames.some(g => g.id === game.id);
  
  if (!isAlreadySelected) {
    setSelectedGames([...selectedGames, game]);
    setQuantities({
      ...quantities,
      [game.id]: 1
    });
  } else {
    setQuantities({
      ...quantities,
      [game.id]: (quantities[game.id] || 1) + 1
    });
  }
  
  setShowAddMoreGames(false);
  showNotification('success', `ÿ™ŸÖÿ™ ÿ•ÿ∂ÿßŸÅÿ© ${game.name} ÿ•ŸÑŸâ ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±`);
};

const increaseQuantity = (gameId) => {
  const game = selectedGames.find(g => g.id === gameId);
  if (!game) return;

  const currentQuantity = quantities[gameId] || 1;
  setQuantities({
    ...quantities,
    [gameId]: currentQuantity + 1
  });
};

  const decreaseQuantity = (gameId) => {
    const currentQuantity = quantities[gameId] || 1;
    if (currentQuantity > 1) {
      setQuantities({
        ...quantities,
        [gameId]: currentQuantity - 1
      });
    } else {
      removeGame(gameId);
    }
  };

  const removeGame = (gameId) => {
    setSelectedGames(selectedGames.filter(game => game.id !== gameId));
    const newQuantities = { ...quantities };
    delete newQuantities[gameId];
    setQuantities(newQuantities);
  };

const handleStartRental = async () => {
  if (!customerName.trim()) {
    showNotification('error', 'ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ•ÿØÿÆÿßŸÑ ÿßÿ≥ŸÖ ÿßŸÑÿπŸÖŸäŸÑ');
    return;
  }
  
  if (!customerPhone.trim()) {
