import React, { useState, useEffect } from 'react';
import authService from '../../services/authService';
import './AdminPages.css';

const ReportsPage = () => {
  const [loading, setLoading] = useState(true);
  const [reports, setReports] = useState({
    dailyRevenue: [],
    topBranches: [],
    topGames: [],
    topCustomers: []
  });
  const [dateRange, setDateRange] = useState({
    start: new Date(new Date().setDate(new Date().getDate() - 30)).toISOString().split('T')[0],
    end: new Date().toISOString().split('T')[0]
  });

  useEffect(() => {
    loadReports();
  }, []);

  const loadReports = async () => {
    try {
      setLoading(true);
      
      // ŸÖÿ≠ÿßŸÉÿßÿ© ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ™ŸÇÿßÿ±Ÿäÿ± ŸÖŸÜ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      const statsResponse = await authService.getDashboardStats();
      const rentalsResponse = await authService.getRentals();
      
      if (statsResponse.success && rentalsResponse.success) {
        // ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÑÿ•ŸÜÿ¥ÿßÿ° ÿ™ŸÇÿßÿ±Ÿäÿ±
        const rentals = rentalsResponse.data;
        
        // ÿ™ŸÇÿ±Ÿäÿ± ÿßŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™ ÿßŸÑŸäŸàŸÖŸäÿ©
        const dailyRevenue = calculateDailyRevenue(rentals);
        
        // ÿ£ŸÅÿ∂ŸÑ ÿßŸÑŸÅÿ±Ÿàÿπ
        const topBranches = calculateTopBranches(rentals);
        
        // ÿ£ŸÅÿ∂ŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®
        const topGames = statsResponse.data.topGames || [];
        
        // ÿ£ŸÅÿ∂ŸÑ ÿßŸÑÿπŸÖŸÑÿßÿ°
        const topCustomers = calculateTopCustomers(rentals);
        
        setReports({
          dailyRevenue,
          topBranches,
          topGames,
          topCustomers
        });
      }
      
    } catch (error) {
      console.error('Error loading reports:', error);
    } finally {
      setLoading(false);
    }
  };

  const calculateDailyRevenue = (rentals) => {
    const revenueByDay = {};
    
    rentals.forEach(rental => {
      if (rental.payment_status === 'ŸÖÿØŸÅŸàÿπ') {
        const date = new Date(rental.created_at).toISOString().split('T')[0];
        revenueByDay[date] = (revenueByDay[date] || 0) + rental.total_amount;
      }
    });
    
    return Object.entries(revenueByDay)
      .map(([date, revenue]) => ({ date, revenue }))
      .sort((a, b) => new Date(b.date) - new Date(a.date))
      .slice(0, 7);
  };

  const calculateTopBranches = (rentals) => {
    const branchRevenue = {};
    
    rentals.forEach(rental => {
      if (rental.payment_status === 'ŸÖÿØŸÅŸàÿπ' && rental.branch_name) {
        branchRevenue[rental.branch_name] = (branchRevenue[rental.branch_name] || 0) + rental.total_amount;
      }
    });
    
    return Object.entries(branchRevenue)
      .map(([branch, revenue]) => ({ branch, revenue }))
      .sort((a, b) => b.revenue - a.revenue)
      .slice(0, 5);
  };

  const calculateTopCustomers = (rentals) => {
    const customerSpending = {};
    
    rentals.forEach(rental => {
      if (rental.payment_status === 'ŸÖÿØŸÅŸàÿπ' && rental.customer_name) {
        customerSpending[rental.customer_name] = (customerSpending[rental.customer_name] || 0) + rental.total_amount;
      }
    });
    
    return Object.entries(customerSpending)
      .map(([customer, spending]) => ({ customer, spending }))
      .sort((a, b) => b.spending - a.spending)
      .slice(0, 5);
  };

  const formatCurrency = (amount) => {
    if (typeof amount !== 'number') return '0.00 ÿ¨';
    return amount.toLocaleString('ar-EG', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }) + ' ÿ¨';
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ŸÇÿßÿ±Ÿäÿ±...</p>
      </div>
    );
  }

  return (
    <div className="admin-page">
      <div className="page-header">
        <div className="header-content">
          <h1>
            <span className="header-icon">üìà</span>
            ÿßŸÑÿ™ŸÇÿßÿ±Ÿäÿ± ÿßŸÑŸÖÿßŸÑŸäÿ©
          </h1>
          <p className="page-subtitle">ÿ™ÿ≠ŸÑŸäŸÑ ÿ¥ÿßŸÖŸÑ ŸÑÿ£ÿØÿßÿ° ÿßŸÑŸÜÿ∏ÿßŸÖ</p>
        </div>
        <div className="header-actions">
          <button className="btn-primary" onClick={loadReports}>
            üîÑ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ™ŸÇÿßÿ±Ÿäÿ±
          </button>
        </div>
      </div>

      <div className="reports-container">
        {/* ÿßŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™ ÿßŸÑŸäŸàŸÖŸäÿ© */}
        <div className="report-section">
          <h2>üìä ÿßŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™ ÿßŸÑŸäŸàŸÖŸäÿ© (ÿ¢ÿÆÿ± 7 ÿ£ŸäÿßŸÖ)</h2>
          <div className="report-card">
            <div className="revenue-chart">
              {reports.dailyRevenue.length === 0 ? (
                <div className="empty-state">
                  <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ®ŸäÿßŸÜÿßÿ™ ŸÑŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™</p>
                </div>
              ) : (
                <div className="revenue-bars">
                  {reports.dailyRevenue.map((day, index) => {
                    const maxRevenue = Math.max(...reports.dailyRevenue.map(d => d.revenue));
                    const height = (day.revenue / maxRevenue) * 150;
                    
                    return (
                      <div key={index} className="revenue-bar-container">
                        <div className="revenue-bar" style={{ height: `${height}px` }}>
                          <div className="revenue-tooltip">
                            {formatCurrency(day.revenue)}
                          </div>
                        </div>
                        <div className="revenue-date">
                          {new Date(day.date).toLocaleDateString('ar-EG', { day: 'numeric', month: 'short' })}
                        </div>
                      </div>
                    );
                  })}
                </div>
              )}
            </div>
            <div className="revenue-summary">
              <div className="summary-item">
                <span className="summary-label">ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑÿ£ÿ≥ÿ®Ÿàÿπ:</span>
                <span className="summary-value">
                  {formatCurrency(reports.dailyRevenue.reduce((sum, day) => sum + day.revenue, 0))}
                </span>
              </div>
              <div className="summary-item">
                <span className="summary-label">ÿßŸÑŸÖÿ™Ÿàÿ≥ÿ∑ ÿßŸÑŸäŸàŸÖŸä:</span>
                <span className="summary-value">
                  {formatCurrency(reports.dailyRevenue.reduce((sum, day) => sum + day.revenue, 0) / reports.dailyRevenue.length || 0)}
                </span>
              </div>
            </div>
          </div>
        </div>

        {/* ÿ£ŸÅÿ∂ŸÑ ÿßŸÑŸÅÿ±Ÿàÿπ */}
        <div className="report-section">
          <h2>üèÜ ÿ£ŸÅÿ∂ŸÑ ÿßŸÑŸÅÿ±Ÿàÿπ ÿ£ÿØÿßÿ°Ÿã</h2>
          <div className="report-card">
            {reports.topBranches.length === 0 ? (
              <div className="empty-state">
                <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ®ŸäÿßŸÜÿßÿ™ ŸÑŸÑŸÅÿ±Ÿàÿπ</p>
              </div>
            ) : (
              <div className="ranking-list">
                {reports.topBranches.map((branch, index) => (
                  <div key={index} className="ranking-item">
                    <div className="rank-info">
                      <span className="rank-number">#{index + 1}</span>
                      <span className="rank-name">{branch.branch}</span>
                    </div>
                    <div className="rank-value">
                      {formatCurrency(branch.revenue)}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* ÿ£ŸÅÿ∂ŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ® */}
        <div className="report-section">
          <h2>üéÆ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑÿ£ŸÉÿ´ÿ± ÿ™ÿ£ÿ¨Ÿäÿ±ÿßŸã</h2>
          <div className="report-card">
            {reports.topGames.length === 0 ? (
              <div className="empty-state">
                <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ®ŸäÿßŸÜÿßÿ™ ŸÑŸÑÿ£ŸÑÿπÿßÿ®</p>
              </div>
            ) : (
              <div className="games-stats">
                {reports.topGames.map((game, index) => (
                  <div key={index} className="game-stat-item">
                    <div className="game-info">
                      <span className="game-rank">#{index + 1}</span>
                      <span className="game-name">{game.name}</span>
                    </div>
                    <div className="game-numbers">
                      <div className="stat">
                        <span className="stat-label">ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™:</span>
                        <span className="stat-value">{game.rental_count}</span>
                      </div>
                      <div className="stat">
                        <span className="stat-label">ÿßŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™:</span>
                        <span className="stat-value">{formatCurrency(game.total_revenue || 0)}</span>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* ÿ£ŸÅÿ∂ŸÑ ÿßŸÑÿπŸÖŸÑÿßÿ° */}
        <div className="report-section">
          <h2>üë• ÿ£ŸÅÿ∂ŸÑ ÿßŸÑÿπŸÖŸÑÿßÿ°</h2>
          <div className="report-card">
            {reports.topCustomers.length === 0 ? (
              <div className="empty-state">
                <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ®ŸäÿßŸÜÿßÿ™ ŸÑŸÑÿπŸÖŸÑÿßÿ°</p>
              </div>
            ) : (
              <div className="customers-list">
                {reports.topCustomers.map((customer, index) => (
                  <div key={index} className="customer-item">
                    <div className="customer-info">
                      <span className="customer-rank">#{index + 1}</span>
                      <span className="customer-name">{customer.customer}</span>
                    </div>
                    <div className="customer-spending">
                      {formatCurrency(customer.spending)}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ReportsPage;
