// ==================== ÿØŸàÿßŸÑ ÿßŸÑŸÖÿ≥ÿßÿπÿØÿ© ŸÑŸÑÿµŸàÿ± ÿßŸÑŸÖÿ≠ÿ≥ŸÜÿ© ====================
const getGameImageUrl = (game) => {
  if (!game) return '/images/default-game.jpg';
  
  // ÿ£ŸàŸÑÿßŸã: ÿßŸÑÿ®ÿ≠ÿ´ ŸÅŸä ÿÆÿ±Ÿäÿ∑ÿ© ÿßŸÑÿµŸàÿ± ÿßŸÑÿ´ÿßÿ®ÿ™ÿ© ÿ®ŸÜÿßÿ°Ÿã ÿπŸÑŸâ ÿßÿ≥ŸÖ ÿßŸÑŸÑÿπÿ®ÿ©
  const gameName = (game.name || '').trim();
  
  // ÿßŸÑÿ®ÿ≠ÿ´ ŸÅŸä ÿßŸÑÿÆÿ±Ÿäÿ∑ÿ© (ŸÖÿπ ŸÖÿ±ÿßÿπÿßÿ© ÿßŸÑÿ≠ÿßŸÑÿ©)
  for (const [key, imagePath] of Object.entries(GAME_IMAGE_MAP)) {
    if (gameName.toLowerCase().includes(key.toLowerCase()) || 
        gameName === key) {
      console.log(`üéØ ÿ™ŸÖÿ™ ŸÖÿ∑ÿßÿ®ŸÇÿ© ÿßŸÑÿµŸàÿ±ÿ© ŸÑŸÑÿπÿ®ÿ© "${gameName}" -> ${imagePath}`);
      return imagePath;
    }
  }
  
  // ÿ´ÿßŸÜŸäÿßŸã: ŸÖÿ≠ÿßŸàŸÑÿ© ÿ•Ÿäÿ¨ÿßÿØ ÿßŸÑÿµŸàÿ± ŸÖŸÜ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
  let imageUrl = game.image_url || game.external_image_url || game.thumbnail_url || game.image;
  
  if (imageUrl) {
    // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑÿ±ÿßÿ®ÿ∑ Ÿäÿ≠ÿ™ŸàŸä ÿπŸÑŸâ http ÿ£Ÿà https
    if (imageUrl.startsWith('http://') || imageUrl.startsWith('https://')) {
      return imageUrl;
    }
    
    // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑÿ±ÿßÿ®ÿ∑ Ÿäÿ®ÿØÿ£ ÿ®ŸÄ /
    if (imageUrl.startsWith('/')) {
      return imageUrl;
    }
    
    // ŸÖÿ≠ÿßŸàŸÑÿ© ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑŸÖÿ≥ÿßÿ± ÿßŸÑÿµÿ≠Ÿäÿ≠
    if (imageUrl.startsWith('uploads/') || imageUrl.startsWith('images/') || imageUrl.startsWith('games/')) {
      return `/${imageUrl}`;
    }
    
    // ŸÖÿ≠ÿßŸàŸÑÿ© ÿ£ÿ≥ŸÖÿßÿ° ÿßŸÑŸÖŸÑŸÅÿßÿ™ ÿßŸÑÿ¥ÿßÿ¶ÿπÿ©
    const commonPaths = [
      `/uploads/${imageUrl}`,
      `/images/${imageUrl}`,
      `/games/${imageUrl}`,
      `/storage/${imageUrl}`,
      `/public/${imageUrl}`
    ];
    
    // ÿßÿÆÿ™ÿ®ÿßÿ± ÿ£ŸàŸÑ ŸÖÿ≥ÿßÿ± ŸÖÿ™ÿßÿ≠
    for (const path of commonPaths) {
      // ŸäŸÖŸÉŸÜ ÿ•ÿ∂ÿßŸÅÿ© ÿßÿÆÿ™ÿ®ÿßÿ± ÿ≠ŸÇŸäŸÇŸä ŸÑŸÑÿµŸàÿ±ÿ© ŸáŸÜÿß ÿ•ÿ∞ÿß ŸÑÿ≤ŸÖ ÿßŸÑÿ£ŸÖÿ±
      console.log(`üîç ŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿ≥ÿßÿ± ÿßŸÑÿµŸàÿ±ÿ©: ${path}`);
      return path;
    }
  }
  
  // ÿ´ÿßŸÑÿ´ÿßŸã: ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑÿµŸàÿ± ÿßŸÑÿπÿßŸÖÿ© ÿ®ŸÜÿßÿ°Ÿã ÿπŸÑŸâ ŸÅÿ¶ÿ© ÿßŸÑŸÑÿπÿ®ÿ©
  const category = (game.category || '').toLowerCase();
  if (category.includes('car') || category.includes('ÿ≥Ÿäÿßÿ±ÿ©')) {
    return '/images/Car.jpg';
  } else if (category.includes('motor') || category.includes('ŸÖŸàÿ™Ÿàÿ≥ŸäŸÉŸÑ')) {
    return '/images/Motor.jpg';
  } else if (category.includes('scooter') || category.includes('ÿ≥ŸÉŸàÿ™ÿ±')) {
    return '/images/Scooter.jpg';
  } else if (category.includes('board') || category.includes('ÿ®Ÿàÿ±ÿØ')) {
    return '/images/hoverboard.jpg';
  } else if (category.includes('trampoline') || category.includes('ÿ™ÿ±ÿßŸÖÿ®ŸàŸÑŸäŸÜ')) {
    return '/images/Trampoline.jpg';
  } else if (category.includes('simulator') || category.includes('ŸÖÿ≠ÿßŸÉŸä')) {
    return '/images/Simulator.jpg';
  }
  
  // ÿ±ÿßÿ®ÿπÿßŸã: ÿßŸÑÿπŸàÿØÿ© ÿ•ŸÑŸâ ÿßŸÑÿµŸàÿ±ÿ© ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ©
  console.log(`‚ö†Ô∏è ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ÿµŸàÿ±ÿ© ŸÑŸÑÿπÿ®ÿ©: "${gameName}"ÿå ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑÿµŸàÿ±ÿ© ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ©`);
  return '/images/default-game.jpg';
};

