    const now = new Date();
    const egyptTime = new Date(now.toLocaleString("en-US", {timeZone: "Africa/Cairo"}));
    
    const hours = egyptTime.getHours();
    const minutes = egyptTime.getMinutes();
    const seconds = egyptTime.getSeconds();
    
    return {
      hours: hours,
      minutes: minutes,
      seconds: seconds,
      formatted: `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`
    };
  };
  
  const formatEgyptDateTime = (dateString) => {
    try {
      if (!dateString) return '--/--/---- --:--:--';
      
      const date = new Date(dateString);
      const egyptDate = new Date(date.toLocaleString("en-US", {timeZone: "Africa/Cairo"}));
      
      const day = egyptDate.getDate().toString().padStart(2, '0');
      const month = (egyptDate.getMonth() + 1).toString().padStart(2, '0');
      const year = egyptDate.getFullYear();
      const hours = egyptDate.getHours().toString().padStart(2, '0');
      const minutes = egyptDate.getMinutes().toString().padStart(2, '0');
      
      return `${day}/${month}/${year} ${hours}:${minutes}`;
    } catch (error) {
      return '--/--/---- --:--:--';
    }
  };
  
  const formatDuration = (minutes) => {
    if (!minutes || minutes === 0) return 'ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠';
    if (minutes < 60) return `${minutes} ÿØŸÇŸäŸÇÿ©`;

    const hours = Math.floor(minutes / 60);
    const remainingMinutes = minutes % 60;

    return `${hours} ÿ≥ÿßÿπÿ©${
