                  </label>
                  <label className="rentals-page-radio-label">
                    <input
                      type="radio"
                      name="durationType"
                      value="open"
                      checked={durationType === 'open'}
                      onChange={(e) => setDurationType(e.target.value)}
                    />
                    <span className="rentals-page-radio-text">ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠</span>
                  </label>
                </div>
                
                {durationType === 'fixed' && (
                  <div className="rentals-page-duration-selector">
                    <label>ÿßÿÆÿ™ÿ± ÿßŸÑŸÖÿØÿ©:</label>
