// src/services/api.js
import axios from 'axios';

// ‚úÖ ÿßŸÑÿ≠ŸÑ: ÿ•ÿ∂ÿßŸÅÿ© /api ÿ•ŸÑŸâ baseURL
const API_BASE_URL = 'http://localhost:5000/api';
const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 30000,
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json',
    'Accept-Language': 'ar'
  }
  // ÿ•ÿ≤ÿßŸÑÿ© withCredentials: true ŸÖÿ§ŸÇÿ™ÿßŸã
});

// Interceptor ŸÑŸÑÿ∑ŸÑÿ®ÿßÿ™
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    
    console.log('üîç ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ™ŸàŸÉŸÜ ŸÅŸä ÿßŸÑÿ∑ŸÑÿ®:', token ? 'ŸÖŸàÿ¨ŸàÿØ' : 'ŸÖŸÅŸÇŸàÿØ');
    
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
      console.log('‚úÖ ÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ™ŸàŸÉŸÜ ÿ•ŸÑŸâ ÿßŸÑŸáŸäÿØÿ±ÿßÿ™:', config.headers.Authorization?.substring(0, 20) + '...');
    } else {
      console.warn('‚ö†Ô∏è  ÿ™ÿ≠ÿ∞Ÿäÿ±: ŸÑÿß ŸäŸàÿ¨ÿØ ÿ™ŸàŸÉŸÜ ŸÅŸä localStorage');
    }
    
    console.log(`üì§ [${config.method?.toUpperCase()}] ${config.baseURL}${config.url}`);
    
    return config;
  },
  (error) => {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ∑ŸÑÿ® API:', error);
    return Promise.reject(error);
  }
);

// Interceptor ŸÑŸÑÿ±ÿØŸàÿØ
api.interceptors.response.use(
  (response) => {
    console.log(`‚úÖ [${response.status}] ${response.config.url}`);
    
    // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ŸáŸäŸÉŸÑ ÿßŸÑÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ©
    const responseData = response.data;
    
    // ÿ•ÿ∞ÿß ŸÉÿßŸÜÿ™ ÿßŸÑÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ÿ™ÿ≠ÿ™ŸàŸä ÿπŸÑŸâ ŸáŸäŸÉŸÑ {success, data, message} ÿßÿ≥ÿ™ÿÆÿØŸÖŸáÿß ŸÉŸÖÿß ŸáŸä
    if (responseData && typeof responseData === 'object' && 'success' in responseData) {
      return responseData;
    }
    
    // ÿ•ÿ∞ÿß ŸÑŸÖ ÿ™ŸÉŸÜ ÿ™ÿ≠ÿ™ŸàŸä ÿπŸÑŸâ ÿßŸÑŸáŸäŸÉŸÑ ÿßŸÑŸÖÿ™ŸàŸÇÿπÿå ÿ∂ÿπŸáÿß ŸÅŸä ŸáŸäŸÉŸÑ ŸÖŸàÿ≠ÿØ
    return {
      success: true,
      status: response.status,
      data: responseData,
      message: 'ÿ™ŸÖ ÿ™ŸÜŸÅŸäÿ∞ ÿßŸÑÿπŸÖŸÑŸäÿ© ÿ®ŸÜÿ¨ÿßÿ≠'
    };
  },
  (error) => {
    console.group('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© API');
    console.log('URL:', error.config?.url);
    console.log('Status:', error.response?.status);
    console.log('Message:', error.message);
    console.log('Response Data:', error.response?.data);
    console.groupEnd();
    
    // ÿ•ŸÜÿ¥ÿßÿ° ŸáŸäŸÉŸÑ ÿÆÿ∑ÿ£ ŸÖŸàÿ≠ÿØ
    let apiError = {
      success: false,
      status: error.response?.status || 0,
      message: '',
      data: error.response?.data,
      error: error
    };
    
    // ÿ™ÿ≠ÿØŸäÿØ ÿ±ÿ≥ÿßŸÑÿ© ÿßŸÑÿÆÿ∑ÿ£ ÿßŸÑŸÖŸÜÿßÿ≥ÿ®ÿ©
    if (error.code === 'ERR_NETWORK') {
      apiError.message = 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ. ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ™ÿ¥ÿ∫ŸäŸÑ ÿßŸÑÿÆÿßÿØŸÖ ÿπŸÑŸâ http://localhost:5000';
    } else if (error.response?.status === 401) {
      apiError.message = 'ÿßŸÜÿ™Ÿáÿ™ ÿ¨ŸÑÿ≥ÿ© ÿßŸÑÿπŸÖŸÑ. Ÿäÿ±ÿ¨Ÿâ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ';
      // ŸÖÿ≥ÿ≠ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≠ŸÑŸäÿ©
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      localStorage.removeItem('lastLogin');
      // ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™Ÿàÿ¨ŸäŸá ŸÑÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ
      if (window.location.pathname !== '/login') {
        window.location.href = '/login';
      }
    } else if (error.response?.status === 404) {
      apiError.message = 'ÿßŸÑŸÖÿ≥ÿßÿ± ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ: ' + error.config?.url;
    } else if (error.response?.status === 500) {
      apiError.message = 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿÆÿßÿØŸÖ';
    } else {
      apiError.message = error.response?.data?.message || error.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ';
    }
    
    return Promise.reject(apiError);
  }
);

// ÿØÿßŸÑÿ© ŸÑÿ™ÿπŸäŸäŸÜ ÿßŸÑÿ™ŸàŸÉŸÜ
api.setToken = (token) => {
  if (token) {
    api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    localStorage.setItem('token', token);
    console.log('‚úÖ ÿ™ŸÖ ÿ™ÿπŸäŸäŸÜ ÿßŸÑÿ™ŸàŸÉŸÜ ÿ®ŸÜÿ¨ÿßÿ≠');
  } else {
    delete api.defaults.headers.common['Authorization'];
    localStorage.removeItem('token');
    console.log('‚úÖ ÿ™ŸÖ ÿ•ÿ≤ÿßŸÑÿ© ÿßŸÑÿ™ŸàŸÉŸÜ');
  }
};

// ÿØÿßŸÑÿ© ŸÑŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ™ŸàŸÉŸÜ
api.checkAuthStatus = () => {
  const token = localStorage.getItem('token');
  const user = localStorage.getItem('user');
  
  console.log('üîç ÿ≠ÿßŸÑÿ© ÿßŸÑŸÖÿµÿßÿØŸÇÿ©:');
  console.log('- ÿßŸÑÿ™ŸàŸÉŸÜ:', token ? `ŸÖŸàÿ¨ŸàÿØ (${token.substring(0, 20)}...)` : 'ŸÖŸÅŸÇŸàÿØ');
  console.log('- ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ:', user ? JSON.parse(user) : 'ÿ∫Ÿäÿ± ŸÖÿ≥ÿ¨ŸÑ');
  
  return {
    isAuthenticated: !!token,
    token: token,
    user: user ? JSON.parse(user) : null
  };
};

