// üîê AUTHENTICATION - ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ£ŸáŸÖ
api.login = async (email, password) => {
  try {
    console.log('üîê ŸÖÿ≠ÿßŸàŸÑÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑ:', email);
    
    // ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ fetch ŸÖÿ®ÿßÿ¥ÿ±ÿ© ŸÑÿ™ŸÅÿßÿØŸä ŸÖÿ¥ÿßŸÉŸÑ ÿßŸÑÿ™ŸàŸÉŸÜ
    const response = await fetch('http://localhost:5000/api/auth/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ 
        email, 
        password
      })
    });
    
    const data = await response.json();
    
    console.log('üîë ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ:', data);
    
    if (data.success && data.token) {
      // ÿ≠ŸÅÿ∏ ÿßŸÑÿ™ŸàŸÉŸÜ
      api.setToken(data.token);
      
      // ÿ≠ŸÅÿ∏ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
      if (data.user) {
        localStorage.setItem('user', JSON.stringify(data.user));
        localStorage.setItem('lastLogin', new Date().toISOString());
        console.log('‚úÖ ÿ™ŸÖ ÿ≠ŸÅÿ∏ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ:', data.user);
      }
      
      return data;
    } else {
      return {
        success: false,
        message: data.message || 'ŸÅÿ¥ŸÑ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ'
      };
    }
  } catch (error) {
    console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ:', error);
    return {
      success: false,
      message: 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ'
    };
  }
};

