// src/components/Layout.jsx
import React from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Layout.css';

const Layout = ({ children }) => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const isAdmin = user?.role === 'admin';
  const isEmployee = user?.role === 'employee' || user?.role === 'branch_manager';

  const adminMenu = [
    { path: '/admin/dashboard', label: 'ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ', icon: 'üìä' },
    { path: '/admin/users', label: 'ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ', icon: 'üë•' },
    { path: '/admin/branches', label: 'ÿßŸÑŸÅÿ±Ÿàÿπ', icon: 'üè¨' },
    { path: '/admin/games', label: 'ÿßŸÑÿ£ŸÑÿπÿßÿ®', icon: 'üéÆ' },
    { path: '/admin/rentals', label: 'ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™', icon: '‚è±Ô∏è' },
    { path: '/admin/reports', label: 'ÿßŸÑÿ™ŸÇÿßÿ±Ÿäÿ±', icon: 'üìà' },
    { path: '/admin/activities', label: 'ÿ≥ÿ¨ŸÑ ÿßŸÑÿ£ŸÜÿ¥ÿ∑ÿ©', icon: 'üìã' },
    { path: '/admin/settings', label: 'ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™', icon: '‚öôÔ∏è' },
  ];

  const employeeMenu = [
    { path: '/employee/rentals', label: 'ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™', icon: 'üéÆ' },
    { path: '/employee/games', label: 'ÿßŸÑÿ£ŸÑÿπÿßÿ®', icon: 'üéØ' },
    { path: '/employee/dashboard', label: 'ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ', icon: 'üìä' },
  ];

  const menu = isAdmin ? adminMenu : employeeMenu;

  return (
    <div className="layout">
      {/* ÿßŸÑÿ¥ÿ±Ÿäÿ∑ ÿßŸÑÿ¨ÿßŸÜÿ®Ÿä */}
      <aside className="sidebar">
        <div className="sidebar-header">
          <div className="logo">
            <span>üéÆ</span>
            <h2>L3BTY</h2>
          </div>
          <div className="user-info">
            <div className="avatar">{user?.name?.charAt(0) || 'U'}</div>
            <div>
              <h4>{user?.name || 'ŸÖÿ≥ÿ™ÿÆÿØŸÖ'}</h4>
              <p>{user?.role === 'admin' ? 'üëë ŸÖÿØŸäÿ±' : 'üë§ ŸÖŸàÿ∏ŸÅ'}</p>
              <p className="branch-info">{user?.branch_name || 'ÿßŸÑŸÅÿ±ÿπ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä'}</p>
            </div>
          </div>
        </div>

        <nav className="sidebar-nav">
          {menu.map((item) => (
            <Link
              key={item.path}
              to={item.path}
              className={`nav-item ${location.pathname === item.path ? 'active' : ''}`}
            >
              <span className="nav-icon">{item.icon}</span>
              <span className="nav-label">{item.label}</span>
            </Link>
          ))}
        </nav>

        <div className="sidebar-footer">
          <button onClick={handleLogout} className="logout-btn">
            <span>üö™</span>
            <span>ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨</span>
          </button>
        </div>
      </aside>

      {/* ÿßŸÑŸÖÿ≠ÿ™ŸàŸâ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä */}
      <main className="main-content">
        <header className="main-header">
          <div className="header-title">
            <h1>{menu.find(item => item.path === location.pathname)?.label || 'ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ'}</h1>
            <p>{new Date().toLocaleDateString('ar-EG', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</p>
          </div>
          <div className="header-actions">
            <button className="notifications-btn">
              üîî
            </button>
          </div>
        </header>

        <div className="content">
          {children}
        </div>
      </main>
    </div>
  );
};

export default Layout;
