// src/services/authService.js - ÿßŸÑŸÜÿ≥ÿÆÿ© ÿßŸÑŸÖÿ≠ÿØÿ´ÿ© ÿßŸÑŸÉÿßŸÖŸÑÿ©
import api from './api';

const authService = {
  // ================= AUTHENTICATION =================
  
  login: async (email, password) => {
    try {
      console.log('üîê ŸÖÿ≠ÿßŸàŸÑÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑ:', email);
      
      const response = await api.login(email, password);
      
      if (response.success) {
        console.log('‚úÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÜÿßÿ¨ÿ≠');
        return {
          success: true,
          data: response.data,
          message: 'ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿ®ŸÜÿ¨ÿßÿ≠'
        };
      } else {
        console.log('‚ùå ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÅÿ¥ŸÑ:', response.message);
        return {
          success: false,
          message: response.message || 'ŸÅÿ¥ŸÑ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ'
        };
      }
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ:', error);
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ. ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ™ÿ¥ÿ∫ŸäŸÑ ÿßŸÑÿÆÿßÿØŸÖ ÿπŸÑŸâ ÿßŸÑŸÖŸÜŸÅÿ∞ 5000'
      };
    }
  },

  logout: () => {
    console.log('üö™ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨...');
    api.logout();
  },

  getCurrentUser: () => {
    try {
      const userStr = localStorage.getItem('user');
      return userStr ? JSON.parse(userStr) : null;
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÑŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ:', error);
      return null;
    }
  },

  getToken: () => {
    const token = localStorage.getItem('token');
    return token ? token.trim() : null;
  },

  isAuthenticated: () => {
    const token = authService.getToken();
    const user = authService.getCurrentUser();
    return !!(token && user);
  },

  // ================= PROFILE MANAGEMENT =================
  
  updateProfile: async (profileData) => {
    try {
      const response = await api.updateProfile(profileData);
      
      if (response.success && response.user) {
        // ÿ™ÿ≠ÿØŸäÿ´ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÅŸä localStorage
        localStorage.setItem('user', JSON.stringify(response.user));
        console.log('‚úÖ ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿ´ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÅŸä localStorage');
      }
      
      return response;
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑŸÖŸÑŸÅ ÿßŸÑÿ¥ÿÆÿµŸä:', error);
      return {
        success: false,
        message: error.message || 'ÿ™ÿπÿ∞ÿ± ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑŸÖŸÑŸÅ ÿßŸÑÿ¥ÿÆÿµŸä'
      };
    }
  },

  changePassword: async (passwordData) => {
    try {
      const response = await api.changePassword(passwordData);
      return response;
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ∫ŸäŸäÿ± ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±:', error);
      return {
        success: false,
        message: error.message || 'ÿ™ÿπÿ∞ÿ± ÿ™ÿ∫ŸäŸäÿ± ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±'
      };
    }
  },

  // ================= BRANCHES MANAGEMENT =================
  
  getBranches: async () => {
    try {
      const response = await api.get('/branches');
      return response;
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑŸÅÿ±Ÿàÿπ:', error);
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÅÿ±Ÿàÿπ',
        data: []
      };
    }
  },

  createBranch: async (branchData) => {
    try {
      const response = await api.post('/branches', branchData);
      return response;
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑŸÅÿ±ÿπ:', error);
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑŸÅÿ±ÿπ'
      };
    }
  },

  updateBranch: async (id, branchData) => {
    try {
      const response = await api.put(`/branches/${id}`, branchData);
      return response;
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑŸÅÿ±ÿπ:', error);
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑŸÅÿ±ÿπ'
      };
    }
  },

  deleteBranch: async (id) => {
    try {
      const response = await api.delete(`/branches/${id}`);
      return response;
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ≠ÿ∞ŸÅ ÿßŸÑŸÅÿ±ÿπ:', error);
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿ≠ÿ∞ŸÅ ÿßŸÑŸÅÿ±ÿπ'
      };
    }
  },

  // ================= GAMES MANAGEMENT =================
  
  getGames: async (params = {}) => {
    try {
      const response = await api.get('/games', { params });
      return response;
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ£ŸÑÿπÿßÿ®:', error);
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®',
        data: [],
        stats: { total: 0, available: 0, rented: 0, maintenance: 0 }
      };
    }
  },

createGame: async (gameData) => {
  try {
    console.log('‚ûï ÿ•ŸÜÿ¥ÿßÿ° ŸÑÿπÿ®ÿ© ÿ¨ÿØŸäÿØÿ©...', gameData);
    
    // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ∑ŸÑŸàÿ®ÿ©
    if (!gameData.name || !gameData.price_per_hour || !gameData.branch_id) {
      return {
        success: false,
        message: 'ÿßŸÑÿßÿ≥ŸÖ ŸàÿßŸÑÿ≥ÿπÿ± ŸàÿßŸÑŸÅÿ±ÿπ ŸÖÿ∑ŸÑŸàÿ®ÿ©'
      };
    }
    
    // ‚ö° ÿ™ŸÜÿ≥ŸäŸÇ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ®ÿØŸàŸÜ deposit_amount
    const formattedData = {
      name: String(gameData.name).trim(),
      description: String(gameData.description || '').trim(),
      category: String(gameData.category || 'ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿäÿ©').trim(),
      image_url: String(gameData.image_url || '').trim(),
      price_per_hour: parseFloat(gameData.price_per_hour) || 0,
      price_per_15min: parseFloat(gameData.price_per_15min) || (parseFloat(gameData.price_per_hour) / 4),
      branch_id: parseInt(gameData.branch_id),
      minimum_age: parseInt(gameData.minimum_age) || 16,  // ‚ö° ŸÅŸÇÿ∑ minimum_age
      max_speed: String(gameData.max_speed || '').trim(),
      battery_life: String(gameData.battery_life || '').trim(),
      weight_limit: String(gameData.weight_limit || '').trim(),
