import React, { useState, useEffect } from 'react';
import authService from '../../services/authService';
import './AdminPages.css';

const BranchesManagement = () => {
  const [branches, setBranches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [newBranch, setNewBranch] = useState({
    name: '',
    location: '',
    city: 'القاهرة',
    contact_phone: '',
    contact_email: '',
    opening_time: '10:00:00',
    closing_time: '22:00:00'
  });

  const loadBranches = async () => {
    try {
      setLoading(true);
      setError('');
      
      const response = await authService.getBranches();
      
      if (response.success) {
        setBranches(response.data);
      } else {
        setError(response.message || 'فشل في تحميل الفروع');
      }
    } catch (error) {
      setError('تعذر الاتصال بالخادم');
      console.error('Error loading branches:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadBranches();
  }, []);

  const handleAddBranch = async () => {
    if (!newBranch.name || !newBranch.location) {
      alert('اسم الفرع والموقع مطلوبان');
      return;
    }

    try {
      const response = await authService.createBranch(newBranch);
      
      if (response.success) {
        alert('تم إنشاء الفرع بنجاح');
        setShowAddModal(false);
        setNewBranch({
          name: '',
          location: '',
          city: 'القاهرة',
          contact_phone: '',
          contact_email: '',
          opening_time: '10:00:00',
          closing_time: '22:00:00'
        });
        loadBranches();
      } else {
        alert(response.message || 'فشل في إنشاء الفرع');
      }
    } catch (error) {
      alert('تعذر الاتصال بالخادم');
    }
  };

  const handleToggleStatus = async (id, currentStatus) => {
    try {
      const response = await authService.updateBranch(id, {
        is_active: currentStatus ? 0 : 1
      });
      
      if (response.success) {
        loadBranches();
      } else {
        alert(response.message || 'فشل في تحديث الحالة');
      }
    } catch (error) {
      alert('تعذر الاتصال بالخادم');
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>جاري تحميل الفروع...</p>
      </div>
    );
  }

  return (
    <div className="admin-page">
      <div className="page-header">
        <h1>إدارة الفروع</h1>
        <button 
          className="btn-add"
          onClick={() => setShowAddModal(true)}
        >
          + إضافة فرع جديد
        </button>
      </div>

      {error && (
        <div className="alert alert-error">
          {error}
        </div>
      )}

      <div className="branches-grid">
        {branches.map(branch => (
          <div key={branch.id} className="branch-card">
            <div className="branch-header">
              <h3>{branch.name}</h3>
              <span className={`status-badge ${branch.is_active ? 'active' : 'inactive'}`}>
                {branch.is_active ? 'نشط' : 'غير نشط'}
              </span>
            </div>
            
            <div className="branch-info">
              <div className="info-item">
                <span className="label">الموقع:</span>
                <span className="value">{branch.location}</span>
              </div>
              
              <div className="info-item">
                <span className="label">المدينة:</span>
                <span className="value">{branch.city}</span>
              </div>
              
              <div className="info-item">
                <span className="label">أوقات العمل:</span>
                <span className="value">
                  {branch.opening_time?.substring(0, 5)} - {branch.closing_time?.substring(0, 5)}
                </span>
              </div>
              
              <div className="info-item">
                <span className="label">الهاتف:</span>
                <span className="value">{branch.contact_phone || 'غير محدد'}</span>
              </div>
              
              <div className="info-item">
                <span className="label">البريد:</span>
                <span className="value">{branch.contact_email || 'غير محدد'}</span>
              </div>
              
              <div className="info-item">
                <span className="label">تاريخ الإنشاء:</span>
                <span className="value">
                  {new Date(branch.created_at).toLocaleDateString('ar-SA')}
                </span>
              </div>
            </div>

            <div className="branch-actions">
              <button 
                className={`btn-status ${branch.is_active ? 'deactivate' : 'activate'}`}
                onClick={() => handleToggleStatus(branch.id, branch.is_active)}
              >
                {branch.is_active ? 'إيقاف' : 'تفعيل'}
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Modal إضافة فرع */}
      {showAddModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2>إضافة فرع جديد</h2>
              <button 
                className="close-btn"
                onClick={() => setShowAddModal(false)}
              >
                ×
              </button>
            </div>
            <div className="modal-body">
              <div className="form-group">
                <label>اسم الفرع *</label>
                <input
                  type="text"
                  value={newBranch.name}
                  onChange={(e) => setNewBranch({...newBranch, name: e.target.value})}
                  placeholder="أدخل اسم الفرع"
                />
              </div>
              
              <div className="form-group">
                <label>الموقع *</label>
                <input
                  type="text"
                  value={newBranch.location}
                  onChange={(e) => setNewBranch({...newBranch, location: e.target.value})}
                  placeholder="أدخل العنوان بالتفصيل"
                />
              </div>
              
              <div className="form-row">
                <div className="form-group">
                  <label>المدينة</label>
                  <input
                    type="text"
                    value={newBranch.city}
                    onChange={(e) => setNewBranch({...newBranch, city: e.target.value})}
                    placeholder="المدينة"
                  />
                </div>
                
                <div className="form-group">
                  <label>الهاتف</label>
                  <input
                    type="text"
                    value={newBranch.contact_phone}
                    onChange={(e) => setNewBranch({...newBranch, contact_phone: e.target.value})}
                    placeholder="رقم الهاتف"
                  />
                </div>
              </div>
              
              <div className="form-row">
                <div className="form-group">
                  <label>وقت الفتح</label>
                  <input
                    type="time"
                    value={newBranch.opening_time?.substring(0, 5)}
                    onChange={(e) => setNewBranch({...newBranch, opening_time: e.target.value + ':00'})}
                  />
                </div>
                
                <div className="form-group">
                  <label>وقت الإغلاق</label>
                  <input
                    type="time"
                    value={newBranch.closing_time?.substring(0, 5)}
                    onChange={(e) => setNewBranch({...newBranch, closing_time: e.target.value + ':00'})}
                  />
                </div>
              </div>
              
              <div className="form-group">
                <label>البريد الإلكتروني</label>
                <input
                  type="email"
                  value={newBranch.contact_email}
                  onChange={(e) => setNewBranch({...newBranch, contact_email: e.target.value})}
                  placeholder="example@domain.com"
                />
              </div>
            </div>
            <div className="modal-footer">
              <button 
                className="btn-cancel"
                onClick={() => setShowAddModal(false)}
              >
                إلغاء
              </button>
              <button 
                className="btn-save"
                onClick={handleAddBranch}
              >
                حفظ
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default BranchesManagement;
