    const games = ['Ø³ÙŠØ§Ø±Ø© ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', 'Ø³ÙƒÙˆØªØ±', 'Ø¯Ø±Ø§Ø¬Ø© Ù†Ø§Ø±ÙŠØ©', 'Ù‡ÙˆÙØ± Ø¨ÙˆØ±Ø¯'];
    const customers = ['Ù…Ø­Ù…Ø¯ Ø£Ø­Ù…Ø¯', 'Ø£Ø­Ù…Ø¯ Ù…Ø­Ù…ÙˆØ¯', 'Ø¹Ù„ÙŠ Ø­Ø³Ù†', 'Ù…Ø­Ù…ÙˆØ¯ Ø®Ø§Ù„Ø¯', 'ÙŠØ§Ø³Ø± Ù…Ø­Ù…Ø¯'];
    const branches = ['Ø§Ù„ÙØ±Ø¹ Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠ', 'ÙØ±Ø¹ Ø§Ù„Ù…Ø¹Ø§Ø¯ÙŠ', 'ÙØ±Ø¹ 6 Ø£ÙƒØªÙˆØ¨Ø±'];
    
    for (let i = 1; i <= 30; i++) {
