      else if (statusRand < 0.3) rentalStatus = 'Ù…Ù„ØºÙŠ';
      
      const amount = 50 + Math.floor(Math.random() * 200);
      const isPaid = Math.random() > 0.2;
      const isOpenTime = Math.random() > 0.7;
      
      mockRentals.push({
