      } else {
        console.warn('âš ï¸ API Ø§Ø³ØªØ¬Ø§Ø¨ ÙˆÙ„ÙƒÙ† Ù…Ø¹ Ø®Ø·Ø£');
        setConnectionStatus('error');
        return false;
      }
    } catch (error) {
      console.error('âŒ ÙØ´Ù„ Ø§Ù„Ø§ØªØµØ§Ù„ Ø¨Ù€ API:', error);
      setConnectionStatus('error');
      setApiError('ØªØ¹Ø°Ø± Ø§Ù„Ø§ØªØµØ§Ù„ Ø¨Ø§Ù„Ø®Ø§Ø¯Ù…. ØªØ£ÙƒØ¯ Ù…Ù† ØªØ´ØºÙŠÙ„ Ø§Ù„Ø®Ø§Ø¯Ù… Ø¹Ù„Ù‰ Ø§Ù„Ù…Ù†ÙØ° 5000');
      return false;
    }
  }, []);

  // ==================== Ø­Ø³Ø§Ø¨ Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª ====================
  const calculateStats = useCallback((rentalsData) => {
    const active = rentalsData.filter(r => r.status === 'Ù†Ø´Ø·').length;
    const completed = rentalsData.filter(r => r.status === 'Ù…ÙƒØªÙ…Ù„').length;
    const cancelled = rentalsData.filter(r => r.status === 'Ù…Ù„ØºÙŠ').length;
    
    const totalRevenue = rentalsData
      .filter(r => r.payment_status === 'Ù…Ø¯ÙÙˆØ¹')
      .reduce((sum, r) => sum + (r.final_amount || r.total_amount || 0), 0);
    
    const today = new Date().toISOString().split('T')[0];
    const todayRevenue = rentalsData
      .filter(r => r.start_time?.split('T')[0] === today && r.payment_status === 'Ù…Ø¯ÙÙˆØ¹')
      .reduce((sum, r) => sum + (r.final_amount || r.total_amount || 0), 0);
    
    setStats({
      totalRentals: rentalsData.length,
      activeRentals: active,
      completedRentals: completed,
      cancelledRentals: cancelled,
      totalRevenue,
      todayRevenue
    });
  }, []);

  // ==================== ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ù…Ù† API ====================
  const loadRentalsFromAPI = useCallback(async () => {
    try {
      console.log('ğŸ“‹ Ø¬Ù„Ø¨ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ù…Ù† API...');
      
      const params = {
        limit: 500,
        include_game: true,
        include_branch: true,
        include_employee: true
      };
      
      if (currentUser?.branch_id) {
        params.branch_id = currentUser.branch_id;
      }
      
      const response = await api.getRentals(params);
      
      if (response?.success && response.data && response.data.length > 0) {
        console.log(`âœ… ØªÙ… Ø¬Ù„Ø¨ ${response.data.length} ØªØ£Ø¬ÙŠØ± Ù…Ù† API`);
        
        // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª
        const processedRentals = response.data.map(rental => ({
          id: rental.id,
          rental_number: rental.rental_number || `#${rental.id}`,
          game_id: rental.game_id,
          game_name: rental.game_name || rental.game?.name || 'Ù„Ø¹Ø¨Ø© ØºÙŠØ± Ù…Ø¹Ø±ÙˆÙØ©',
          customer_name: rental.customer_name || 'Ø¹Ù…ÙŠÙ„',
          customer_phone: rental.customer_phone || '01000000000',
          employee_name: rental.employee_name || rental.employee?.name || 'Ù…ÙˆØ¸Ù',
          branch_id: rental.branch_id,
          branch_name: rental.branch_name || rental.branch?.name || `ÙØ±Ø¹ ${rental.branch_id}`,
          shift_id: rental.shift_id,
          start_time: rental.start_time,
          end_time: rental.end_time,
          rental_type: rental.rental_type || (rental.is_open_time === 1 ? 'open' : 'fixed'),
          is_open_time: rental.is_open_time || 0,
          duration_minutes: rental.duration_minutes || 15,
          total_amount: parseFloat(rental.total_amount) || 0,
          final_amount: parseFloat(rental.final_amount) || parseFloat(rental.total_amount) || 0,
          paid_amount: parseFloat(rental.paid_amount) || 0,
          payment_status: rental.payment_status || 'ØºÙŠØ± Ù…Ø¯ÙÙˆØ¹',
          status: rental.status || 'Ù†Ø´Ø·',
          notes: rental.notes || ''
        }));
        
        setRentals(processedRentals);
        setFilteredRentals(processedRentals);
        calculateStats(processedRentals);
        
        // Ø§Ø³ØªØ®Ø±Ø§Ø¬ Ø§Ù„Ø´ÙŠÙØªØ§Øª Ù…Ù† Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
        const extractedShifts = generateMockShifts(processedRentals);
        setShifts(extractedShifts);
        
        return true;
      } else {
        console.warn('âš ï¸ API Ø§Ø³ØªØ¬Ø§Ø¨ ÙˆÙ„ÙƒÙ† Ø¨Ø¯ÙˆÙ† Ø¨ÙŠØ§Ù†Ø§Øª');
        return false;
      }
    } catch (error) {
      console.error('âŒ ÙØ´Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª:', error);
      return false;
    }
  }, [currentUser, calculateStats]);

  // ==================== ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª (Ù…Ø¹ Ø®Ø·Ø© Ø¨Ø¯ÙŠÙ„Ø©) ====================
  const loadData = useCallback(async () => {
    setLoading(true);
    setApiError(null);
    
    try {
      // 1. Ø§Ù„ØªØ­Ù‚Ù‚ Ù…Ù† Ø§ØªØµØ§Ù„ API
      const isConnected = await checkApiConnection();
      
      // 2. ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù… (Ø­ØªÙ‰ Ù„Ùˆ ÙØ´Ù„ Ø§Ù„Ø§ØªØµØ§Ù„)
      const user = api.getCurrentUser();
