          }
        } catch (error) {
          console.warn('âš ï¸ ÙØ´Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø´ÙŠÙØª:', error);
        }
        
        // ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
        dataLoaded = await loadRentalsFromAPI();
