import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../../context/AuthContext';
import authService from '../../services/authService';
import './GamesManagement.css';
import api from '../../services/api';

const GamesManagement = () => {
  const { user } = useAuth();
  const [allGames, setAllGames] = useState([]); // ÿ¨ŸÖŸäÿπ ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸÖŸÜ ÿ¨ŸÖŸäÿπ ÿßŸÑŸÅÿ±Ÿàÿπ
  const [currentGames, setCurrentGames] = useState([]); // ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿπÿ±Ÿàÿ∂ÿ© ÿ≠ÿßŸÑŸäÿßŸã
  const [branches, setBranches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [loadingBranch, setLoadingBranch] = useState(false);
  const [error, setError] = useState('');
  const [selectedBranch, setSelectedBranch] = useState('all');
  const [branchStats, setBranchStats] = useState({});

  useEffect(() => {
    loadGamesData();
  }, []);

  // ‚úÖ ÿØÿßŸÑÿ© ŸÖÿ≠ÿ≥ŸÜÿ© ŸÑÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÖÿπ ÿ™ÿ≠ÿØŸäÿ´ ÿ™ŸÑŸÇÿßÿ¶Ÿä
  const loadGamesData = async (forceRefresh = false) => {
    try {
      setLoading(true);
      setError('');

      // ‚úÖ ÿ¨ŸÑÿ® ÿßŸÑŸÅÿ±Ÿàÿπ ÿ£ŸàŸÑÿßŸã
      const branchesResponse = await authService.getBranches();
      
      if (branchesResponse.success) {
        const branchesData = branchesResponse.data || [];
        console.log('üè¨ ÿπÿØÿØ ÿßŸÑŸÅÿ±Ÿàÿπ ÿßŸÑŸÖÿ≠ŸÖŸÑÿ©:', branchesData.length);
        setBranches(branchesData);
        
        // ‚úÖ ÿ™ŸáŸäÿ¶ÿ© ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ŸÑŸÉŸÑ ŸÅÿ±ÿπ
        const initialStats = {};
        branchesData.forEach(branch => {
          initialStats[branch.id] = {
            branch_id: branch.id,
            branch_name: branch.name,
            total_games: 0,
            gameCounts: {}
          };
        });
        
        setBranchStats(initialStats);
        
        // ‚úÖ ÿ¨ŸÑÿ® ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸÑŸÉŸÑ ŸÅÿ±ÿπ ÿ®ÿ¥ŸÉŸÑ ŸÖŸÜŸÅÿµŸÑ
        const allGamesData = [];
        
        // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖÿØŸäÿ±ÿßŸãÿå ÿ¨ŸÑÿ® ÿ¨ŸÖŸäÿπ ÿßŸÑÿ£ŸÑÿπÿßÿ®
        if (user?.role === 'admin') {
          console.log('üëë ÿßŸÑŸÖÿØŸäÿ±: ÿ¨ŸÑÿ® ÿ¨ŸÖŸäÿπ ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸÖŸÜ ÿ¨ŸÖŸäÿπ ÿßŸÑŸÅÿ±Ÿàÿπ');
          
          try {
            // ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ÿßŸÑÿ£ŸàŸÑŸâ: ÿ¨ŸÑÿ® ÿ¨ŸÖŸäÿπ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿØŸÅÿπÿ© Ÿàÿßÿ≠ÿØÿ©
            const gamesResponse = await api.get('/games', {
              params: { include_branch_info: 'true' }
            });
            
            if (gamesResponse.success) {
              const gamesData = gamesResponse.data || [];
              console.log(`‚úÖ ÿ™ŸÖ ÿ¨ŸÑÿ® ${gamesData.length} ŸÑÿπÿ®ÿ© ŸÖŸÜ ÿ¨ŸÖŸäÿπ ÿßŸÑŸÅÿ±Ÿàÿπ`);
              allGamesData.push(...gamesData);
            } else {
              console.warn('‚ö†Ô∏è ŸÅÿ¥ŸÑ ÿ¨ŸÑÿ® ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿØŸÅÿπÿ© Ÿàÿßÿ≠ÿØÿ©ÿå ÿ¨ÿ±ÿ® ÿ∑ÿ±ŸäŸÇÿ© ŸÅÿ±ÿØŸäÿ©');
              throw new Error('ŸÅÿ¥ŸÑ ÿ¨ŸÑÿ® ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿØŸÅÿπÿ© Ÿàÿßÿ≠ÿØÿ©');
            }
          } catch (batchError) {
            console.log('üîÑ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ÿßŸÑÿ´ÿßŸÜŸäÿ©: ÿ¨ŸÑÿ® ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸÑŸÉŸÑ ŸÅÿ±ÿπ ÿπŸÑŸâ ÿ≠ÿØÿ©');
            
            // ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ÿßŸÑÿ´ÿßŸÜŸäÿ©: ÿ¨ŸÑÿ® ÿ£ŸÑÿπÿßÿ® ŸÉŸÑ ŸÅÿ±ÿπ ÿπŸÑŸâ ÿ≠ÿØÿ©
            for (const branch of branchesData) {
              try {
                const branchGamesResponse = await api.get('/games', {
                  params: { 
                    branch_id: branch.id,
                    include_branch_info: 'true'
                  }
                });
                
                if (branchGamesResponse.success) {
                  const branchGames = branchGamesResponse.data || [];
                  console.log(`‚úÖ ŸÅÿ±ÿπ ${branch.name}: ${branchGames.length} ŸÑÿπÿ®ÿ©`);
                  allGamesData.push(...branchGames);
                }
              } catch (branchError) {
                console.warn(`‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿ£ŸÑÿπÿßÿ® ŸÅÿ±ÿπ ${branch.name}:`, branchError.message);
              }
            }
          }
        } else {
          // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖŸàÿ∏ŸÅÿßŸãÿå ÿ¨ŸÑÿ® ÿ£ŸÑÿπÿßÿ® ŸÅÿ±ÿπŸá ŸÅŸÇÿ∑
          console.log(`üë§ ÿßŸÑŸÖŸàÿ∏ŸÅ: ÿ¨ŸÑÿ® ÿ£ŸÑÿπÿßÿ® ŸÅÿ±ÿπ ${user.branch_id}`);
          
          try {
            const gamesResponse = await api.get('/games', {
              params: { 
                branch_id: user.branch_id,
                include_branch_info: 'true'
              }
            });
            
            if (gamesResponse.success) {
              const gamesData = gamesResponse.data || [];
              console.log(`‚úÖ ÿ™ŸÖ ÿ¨ŸÑÿ® ${gamesData.length} ŸÑÿπÿ®ÿ© ŸÑŸÅÿ±ÿπŸÉ`);
              allGamesData.push(...gamesData);
            }
          } catch (branchError) {
            console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿ£ŸÑÿπÿßÿ® ŸÅÿ±ÿπŸÉ:', branchError);
          }
        }
        
        console.log('üéÆ ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ≠ŸÖŸÑÿ©:', allGamesData.length);
        
        // ‚úÖ ÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÜŸáÿßÿ¶Ÿäÿ©
        const finalStats = { ...initialStats };
        let totalCount = 0;
        
        allGamesData.forEach(game => {
          const branchId = game.branch_id;
          if (finalStats[branchId]) {
            finalStats[branchId].total_games++;
            totalCount++;
            
            // ÿ≤ŸäÿßÿØÿ© ÿπÿØÿØ ŸÉŸÑ ŸÜŸàÿπ ŸÖŸÜ ÿßŸÑÿ£ŸÑÿπÿßÿ®
            const gameName = game.name;
            finalStats[branchId].gameCounts[gameName] = (finalStats[branchId].gameCounts[gameName] || 0) + 1;
          } else {
            console.warn(`‚ö†Ô∏è ŸÑÿπÿ®ÿ© ŸÖÿ±ÿ™ÿ®ÿ∑ÿ© ÿ®ŸÅÿ±ÿπ ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ: ${game.name} - ŸÅÿ±ÿπ ID: ${branchId}`);
            
            // ÿ•ŸÜÿ¥ÿßÿ° ÿ•ÿØÿÆÿßŸÑ ŸÑŸÑŸÅÿ±ÿπ ÿ∫Ÿäÿ± ÿßŸÑŸÖŸàÿ¨ŸàÿØ
            if (!finalStats[branchId]) {
              finalStats[branchId] = {
                branch_id: branchId,
                branch_name: `ŸÅÿ±ÿπ ${branchId}`,
                total_games: 0,
                gameCounts: {}
              };
            }
            
            finalStats[branchId].total_games++;
            totalCount++;
            
            const gameName = game.name;
            finalStats[branchId].gameCounts[gameName] = (finalStats[branchId].gameCounts[gameName] || 0) + 1;
          }
        });
        
        // ‚úÖ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ≠ÿßŸÑÿ©
        setAllGames(allGamesData);
        setCurrentGames(allGamesData);
        setBranchStats(finalStats);
        
        console.log('üìä ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÜŸáÿßÿ¶Ÿäÿ©:', {
          totalBranches: branchesData.length,
          totalGames: totalCount,
          branchStats: finalStats
        });
        
      } else {
        console.error('‚ùå ŸÅÿ¥ŸÑ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÅÿ±Ÿàÿπ:', branchesResponse.message);
        setError('ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÅÿ±Ÿàÿπ: ' + (branchesResponse.message || ''));
      }

    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ÿ¥ÿßŸÖŸÑ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ£ŸÑÿπÿßÿ®:', error);
      setError('ÿ™ÿπÿ∞ÿ± ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ£ŸÑÿπÿßÿ®. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ.');
      setAllGames([]);
      setCurrentGames([]);
      setBranches([]);
      setBranchStats({});
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ ÿØÿßŸÑÿ© ŸÖÿ≠ÿ≥ŸÜÿ© ŸÑÿ¨ŸÑÿ® ÿ£ŸÑÿπÿßÿ® ŸÅÿ±ÿπ ŸÖÿπŸäŸÜ
  const loadBranchGamesOnly = useCallback(async (branchId) => {
    try {
      setLoadingBranch(true);
      console.log(`üîÑ ÿ¨ŸÑÿ® ÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÅÿ±ÿπ ${branchId} ŸÅŸÇÿ∑...`);
      
      const response = await api.get('/games', {
        params: {
          branch_id: branchId,
          include_branch_info: 'true'
        }
      });
      
      if (response.success) {
        const branchGames = response.data || [];
        console.log(`‚úÖ ÿ™ŸÖ ÿ¨ŸÑÿ® ${branchGames.length} ŸÑÿπÿ®ÿ© ŸÑŸÑŸÅÿ±ÿπ ${branchId}`);
        
        // ‚úÖ ÿ™ÿ≠ÿØŸäÿ´ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÅÿ±ÿπ
        const updatedStats = { ...branchStats };
        if (updatedStats[branchId]) {
          updatedStats[branchId].total_games = branchGames.length;
          
          // ÿ™ÿ≠ÿØŸäÿ´ gameCounts
          const gameCounts = {};
          branchGames.forEach(game => {
            const gameName = game.name;
            gameCounts[gameName] = (gameCounts[gameName] || 0) + 1;
          });
          updatedStats[branchId].gameCounts = gameCounts;
        }
        
        setBranchStats(updatedStats);
        setCurrentGames(branchGames);
      } else {
        console.warn('‚ö†Ô∏è ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≠ŸÑŸäÿ© ŸÉÿ®ÿØŸäŸÑ');
        const branchGames = allGames.filter(game => game.branch_id == branchId);
        setCurrentGames(branchGames);
      }
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÅÿ±ÿπ:', error);
      // ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≠ŸÑŸäÿ© ŸÉÿ®ÿØŸäŸÑ
      const branchGames = allGames.filter(game => game.branch_id == branchId);
      setCurrentGames(branchGames);
    } finally {
      setLoadingBranch(false);
    }
  }, [allGames, branchStats]);

  // ‚úÖ ÿπŸÜÿØ ÿßÿÆÿ™Ÿäÿßÿ± ŸÅÿ±ÿπ ŸÖÿπŸäŸÜ
  const handleSelectBranch = useCallback((branchId) => {
    setSelectedBranch(branchId);
    
    if (branchId === 'all') {
      setCurrentGames(allGames);
    } else {
      // ÿ™ÿ≠ŸÖŸäŸÑ ÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÅÿ±ÿπ ÿßŸÑŸÖÿ≠ÿØÿØ
      loadBranchGamesOnly(branchId);
    }
  }, [allGames, loadBranchGamesOnly]);

  // ‚úÖ ÿ™ÿ≠ŸÖŸäŸÑ ÿ™ŸÑŸÇÿßÿ¶Ÿä ÿπŸÜÿØ ÿ™ÿ∫ŸäŸäÿ± selectedBranch
  useEffect(() => {
    if (selectedBranch !== 'all') {
      loadBranchGamesOnly(selectedBranch);
    }
  }, [selectedBranch, loadBranchGamesOnly]);

  // ‚úÖ ÿØÿßŸÑÿ© ŸÑÿ™ÿ≠ÿØŸäÿ´ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ŸÅÿ±ÿπ ŸÖÿπŸäŸÜ
  const updateBranchStats = useCallback((branchId, gamesCount, gamesList = []) => {
    setBranchStats(prev => {
      const newStats = { ...prev };
      
      if (newStats[branchId]) {
        newStats[branchId].total_games = gamesCount;
        
        // ÿ™ÿ≠ÿØŸäÿ´ gameCounts
        const gameCounts = {};
        gamesList.forEach(game => {
          const gameName = game.name;
          gameCounts[gameName] = (gameCounts[gameName] || 0) + 1;
        });
        newStats[branchId].gameCounts = gameCounts;
      }
      
      return newStats;
    });
  }, []);

  // ‚úÖ ÿØÿßŸÑÿ© ŸÑÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿπÿØÿØ ÿßŸÑÿ•ÿ¨ŸÖÿßŸÑŸä ŸÑŸÑÿ£ŸÑÿπÿßÿ®
  const refreshTotalGamesCount = useCallback(() => {
    const totalGamesCount = allGames.length;
    console.log(`üîÑ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿπÿØÿØ ÿßŸÑÿ•ÿ¨ŸÖÿßŸÑŸä ŸÑŸÑÿ£ŸÑÿπÿßÿ®: ${totalGamesCount}`);
    return totalGamesCount;
  }, [allGames]);

  const calculateTotalStats = () => {
    // ‚úÖ ÿ≠ÿ≥ÿßÿ® ÿØŸÇŸäŸÇ ŸÑŸÑÿ£ŸÑÿπÿßÿ® ŸÖŸÜ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÖÿÆÿ≤ŸÜÿ©
    let totalGamesCount = 0;
    Object.values(branchStats).forEach(stat => {
      totalGamesCount += stat.total_games || 0;
    });
    
    return {
      totalGames: totalGamesCount,
      totalBranches: branches.length
    };
  };

  // ‚úÖ ÿØÿßŸÑÿ© ŸÑŸÅÿ≠ÿµ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÅÿ±ÿπ
  const checkBranchData = useCallback((branchId) => {
    const branch = branches.find(b => b.id == branchId);
    const stats = branchStats[branchId];
    const games = allGames.filter(g => g.branch_id == branchId);
    
    console.log(`üîç ŸÅÿ≠ÿµ ÿ®ŸäÿßŸÜÿßÿ™ ŸÅÿ±ÿπ ${branchId}:`, {
      branchExists: !!branch,
      branchName: branch?.name,
      statsTotalGames: stats?.total_games || 0,
      actualGamesCount: games.length,
      gamesList: games.map(g => ({ id: g.id, name: g.name }))
    });
    
    return {
      branchExists: !!branch,
      branchName: branch?.name,
      statsTotalGames: stats?.total_games || 0,
      actualGamesCount: games.length,
      gamesList: games
    };
  }, [branches, branchStats, allGames]);

  if (loading) {
    return (
      <div className="games-management-page">
        <div className="loading-container">
          <div className="spinner"></div>
          <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸÖŸÜ ÿ¨ŸÖŸäÿπ ÿßŸÑŸÅÿ±Ÿàÿπ...</p>
        </div>
      </div>
    );
  }

  const totals = calculateTotalStats();
  const selectedBranchData = selectedBranch !== 'all' ? branchStats[selectedBranch] : null;

  return (
    <div className="games-management-page">
      {/* ÿ±ÿ£ÿ≥ ÿßŸÑÿµŸÅÿ≠ÿ© */}
      <div className="page-header">
        <div className="header-left">
          <h1>üéÆ ŸÖÿÆÿ≤ŸàŸÜ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿ≠ÿ≥ÿ® ÿßŸÑŸÅÿ±Ÿàÿπ</h1>
          <p className="page-description">ÿπÿ±ÿ∂ ÿ™Ÿàÿ≤Ÿäÿπ ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸàÿπÿØÿØ ÿßŸÑŸÇÿ∑ÿπ ŸÅŸä ŸÉŸÑ ŸÅÿ±ÿπ</p>
        </div>
        <div className="header-right">
          <button
            className="btn btn-secondary"
            onClick={() => loadGamesData(true)}
            disabled={loading}
          >
            {loading ? '‚è≥ ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ÿØŸäÿ´...' : 'üîÑ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™'}
          </button>
          <button
            className="btn btn-info"
            onClick={() => {
              console.log('üìä ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÜÿ∏ÿßŸÖ:', {
                allGamesCount: allGames.length,
                branchesCount: branches.length,
                branchStats: branchStats,
                totals: totals
              });
              alert(`üìä ŸÖŸÑÿÆÿµ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™:\n\nÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑÿ£ŸÑÿπÿßÿ®: ${totals.totalGames}\nÿπÿØÿØ ÿßŸÑŸÅÿ±Ÿàÿπ: ${totals.totalBranches}\n\nÿßŸÜÿ∏ÿ± console ŸÑŸÑŸÖÿ≤ŸäÿØ ŸÖŸÜ ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ`);
            }}
            style={{ marginLeft: '10px' }}
          >
            üîç ŸÅÿ≠ÿµ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
          </button>
        </div>
      </div>

      {error && (
        <div className="alert alert-error">
          <span>‚ö†Ô∏è {error}</span>
          <button onClick={() => loadGamesData(true)}>ÿ•ÿπÿßÿØÿ© ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ©</button>
        </div>
      )}

      {/* ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿ•ÿ¨ŸÖÿßŸÑŸäÿ© ŸÖÿ®ÿ≥ÿ∑ÿ© */}
      <div className="total-stats">
        <h2>üìä ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ•ÿ¨ŸÖÿßŸÑŸäÿ©</h2>
        <div className="stats-grid">
          <div className="stat-card stat-primary">
            <div className="stat-icon">üè¨</div>
            <div className="stat-content">
              <h3>ÿπÿØÿØ ÿßŸÑŸÅÿ±Ÿàÿπ</h3>
              <p className="stat-value">{totals.totalBranches}</p>
              <p className="stat-subtext">ŸÅÿ±Ÿàÿπ ŸÜÿ¥ÿ∑ÿ©</p>
            </div>
          </div>

          <div className="stat-card stat-success">
            <div className="stat-icon">üéÆ</div>
            <div className="stat-content">
              <h3>ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑÿ£ŸÑÿπÿßÿ®</h3>
              <p className="stat-value">{totals.totalGames}</p>
              <p className="stat-subtext">ŸÇÿ∑ÿπÿ© ŸÅŸä ÿ¨ŸÖŸäÿπ ÿßŸÑŸÅÿ±Ÿàÿπ</p>
            </div>
          </div>
        </div>
      </div>

      {/* ŸÅŸÑÿ™ÿ±ÿ© ÿ≠ÿ≥ÿ® ÿßŸÑŸÅÿ±ÿπ */}
      <div className="branch-filter">
        <h2>üè¨ ÿßÿÆÿ™ÿ± ÿßŸÑŸÅÿ±ÿπ ŸÑÿπÿ±ÿ∂ ÿ™ŸÅÿßÿµŸäŸÑŸá</h2>
        <div className="filter-controls">
          <select
            value={selectedBranch}
            onChange={(e) => handleSelectBranch(e.target.value)}
            className="branch-select"
            disabled={loading || loadingBranch}
          >
            <option value="all">üìã ÿ¨ŸÖŸäÿπ ÿßŸÑŸÅÿ±Ÿàÿπ ({totals.totalGames} ŸÑÿπÿ®ÿ©)</option>
            {branches.map(branch => {
              const branchStat = branchStats[branch.id] || {};
              const gameCount = branchStat.total_games || 0;
              return (
                <option key={branch.id} value={branch.id}>
                  {branch.name} - {gameCount} ŸÑÿπÿ®ÿ©
                </option>
              );
            })}
          </select>

          <div className="filter-actions">
            <button
              className="btn btn-outline"
              onClick={() => handleSelectBranch('all')}
              disabled={selectedBranch === 'all' || loading}
            >
              ÿπÿ±ÿ∂ ÿßŸÑŸÉŸÑ
            </button>
            {selectedBranch !== 'all' && (
              <button
                className="btn btn-warning"
                onClick={() => checkBranchData(selectedBranch)}
                style={{ marginLeft: '10px' }}
              >
                üîç ŸÅÿ≠ÿµ ÿßŸÑŸÅÿ±ÿπ
              </button>
            )}
            {loadingBranch && (
              <span className="loading-text">‚è≥ ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÅÿ±ÿπ...</span>
            )}
          </div>
        </div>
      </div>

      {/* ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÅÿ±ÿπ ÿßŸÑŸÖÿ≠ÿØÿØ - ŸÖÿ®ÿ≥ÿ∑ÿ© */}
      {selectedBranch !== 'all' && selectedBranchData && (
        <div className="branch-details">
          <div className="branch-header-card">
            <div className="branch-header-top">
              <h2>üè¨ {selectedBranchData.branch_name}</h2>
              <div className="branch-actions">
                <span className="branch-id-display">ID: {selectedBranch}</span>
                <button
                  className="btn btn-small btn-primary"
                  onClick={() => loadBranchGamesOnly(selectedBranch)}
                  disabled={loadingBranch}
                  style={{ marginRight: '10px' }}
                >
                  {loadingBranch ? '‚è≥' : 'üîÑ'} ÿ™ÿ≠ÿØŸäÿ´
                </button>
              </div>
            </div>

            <div className="branch-summary">
              <div className="summary-card">
                <div className="summary-icon">üéÆ</div>
                <div className="summary-content">
                  <h4>ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑÿ£ŸÑÿπÿßÿ®</h4>
                  <p className="summary-value large">{selectedBranchData.total_games || 0}</p>
                  <p className="summary-subtext">ŸÇÿ∑ÿπÿ© ŸÅŸä ÿßŸÑŸÅÿ±ÿπ</p>
                </div>
              </div>
            </div>
          </div>

          {/* ÿπÿØÿØ ŸÉŸÑ ŸÇÿ∑ÿπÿ© ŸÖŸÜ ÿßŸÑÿ£ŸÑÿπÿßÿ® */}
          {selectedBranchData.gameCounts && Object.keys(selectedBranchData.gameCounts).length > 0 && (
            <div className="game-pieces-section">
              <h3>üéØ ÿ™Ÿàÿ≤Ÿäÿπ ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸÅŸä ÿßŸÑŸÅÿ±ÿπ</h3>
              <div className="section-description">
                ÿπÿØÿØ ÿßŸÑŸÇÿ∑ÿπ ŸÑŸÉŸÑ ŸÜŸàÿπ ŸÖŸÜ ÿßŸÑÿ£ŸÑÿπÿßÿ®
              </div>
              <div className="game-pieces-grid">
                {Object.entries(selectedBranchData.gameCounts)
                  .sort((a, b) => b[1] - a[1]) // ÿ™ÿ±ÿ™Ÿäÿ® ÿ™ŸÜÿßÿ≤ŸÑŸä ÿ≠ÿ≥ÿ® ÿßŸÑÿπÿØÿØ
                  .map(([gameName, count]) => {
                    const percentage = selectedBranchData.total_games > 0 ?
                      (count / selectedBranchData.total_games) * 100 : 0;
                    return (
                      <div key={gameName} className="game-piece-card">
                        <div className="game-piece-header">
                          <div className="game-name-container">
                            <span className="game-name">{gameName}</span>
                            <span className="game-count">{count} ŸÇÿ∑ÿπÿ©</span>
                          </div>
                          <span className="percentage">{Math.round(percentage)}%</span>
                        </div>
                        <div className="pieces-bar-container">
                          <div className="pieces-bar">
                            <div
                              className="pieces-fill"
                              style={{ width: `${percentage}%` }}
                            ></div>
                          </div>
                        </div>
                      </div>
                    );
                  })}
              </div>
            </div>
          )}
        </div>
      )}

      {/* ÿπÿ±ÿ∂ ÿ¨ŸÖŸäÿπ ÿßŸÑŸÅÿ±Ÿàÿπ - ŸÖÿ®ÿ≥ÿ∑ */}
      <div className="all-branches-section">
        <div className="section-header">
          <h2>üè¨ ÿ™Ÿàÿ≤Ÿäÿπ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿπŸÑŸâ ÿ¨ŸÖŸäÿπ ÿßŸÑŸÅÿ±Ÿàÿπ</h2>
          <span className="section-subtitle">{branches.length} ŸÅÿ±ÿπ</span>
        </div>

        {branches.length > 0 ? (
          <div className="branches-grid">
            {branches.map(branch => {
              const stats = branchStats[branch.id] || {};
              const mostCommonGame = stats.gameCounts ?
                Object.entries(stats.gameCounts).sort((a, b) => b[1] - a[1])[0] : null;

              return (
                <div
                  key={branch.id}
                  className={`branch-card ${selectedBranch == branch.id ? 'selected' : ''}`}
                  onClick={() => handleSelectBranch(branch.id)}
                  style={{ cursor: 'pointer' }}
                >
                  <div className="branch-card-header">
                    <div className="branch-title">
                      <h3>{branch.name}</h3>
                      <span className="branch-id">ID: {branch.id}</span>
                    </div>
                    <div className="branch-total">
                      <span className="total-label">ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑÿ£ŸÑÿπÿßÿ®</span>
                      <span className="total-value">{stats.total_games || 0}</span>
                    </div>
                  </div>

                  <div className="branch-card-body">
                    {mostCommonGame && (
                      <div className="most-common-game">
                        <div className="common-game-label">
                          <span className="icon">üèÜ</span>
                          <span>ÿßŸÑÿ£ŸÉÿ´ÿ± ÿ™Ÿàÿßÿ¨ÿØÿßŸã</span>
                        </div>
                        <div className="common-game-info">
                          <span className="game-name">{mostCommonGame[0]}</span>
                          <span className="game-count">{mostCommonGame[1]} ŸÇÿ∑ÿπÿ©</span>
                        </div>
                      </div>
                    )}
                  </div>

                  <div className="branch-card-footer">
                    <div className="view-branch-btn">
                      {selectedBranch == branch.id ?
                        'üìä ŸÖÿπÿ±Ÿàÿ∂ ÿ≠ÿßŸÑŸäÿßŸã' :
                        'üìä ÿßŸÜŸÇÿ± ŸÑŸÑÿπÿ±ÿ∂'}
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        ) : (
          <div className="empty-state">
            <div className="empty-icon">üè¨</div>
            <h3>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÅÿ±Ÿàÿπ</h3>
            <p>ŸÑŸÖ Ÿäÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ÿ£Ÿä ŸÅÿ±Ÿàÿπ ÿ®ÿπÿØ</p>
          </div>
        )}
      </div>

      {/* ÿ¨ÿØŸàŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ® - ŸÖÿ®ÿ≥ÿ∑ */}
      <div className="games-table-section">
        <div className="section-header">
          <h2>
            üìã {selectedBranch !== 'all' ?
              `ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸÅŸä ${selectedBranchData?.branch_name}` :
              'ŸÇÿßÿ¶ŸÖÿ© ÿ¨ŸÖŸäÿπ ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸÅŸä ÿ¨ŸÖŸäÿπ ÿßŸÑŸÅÿ±Ÿàÿπ'}
          </h2>
          <span className="section-subtitle">
            {currentGames.length} ŸÑÿπÿ®ÿ©
            {loadingBranch && ' ‚è≥ ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ...'}
          </span>
        </div>

        {currentGames.length > 0 ? (
          <div className="games-table-container">
            <table className="games-table">
              <thead>
                <tr>
                  <th>#</th>
                  <th>ÿßŸÑŸÑÿπÿ®ÿ©</th>
                  <th>ÿßŸÑŸÅÿ¶ÿ©</th>
                  <th>ÿßŸÑŸÅÿ±ÿπ</th>
                  <th>ÿßŸÑÿ≥ÿπÿ± / ÿ≥ÿßÿπÿ©</th>
                </tr>
              </thead>
              <tbody>
                {currentGames.map((game, index) => (
                  <tr key={game.id || index}>
                    <td className="text-center">{index + 1}</td>
                    <td>
                      <div className="game-info">
                        <div className="game-name">{game.name}</div>
                        {game.description && (
                          <div className="game-description">{game.description}</div>
                        )}
                      </div>
                    </td>
                    <td>
                      <span className="category-badge">
                        {game.category || 'ÿ∫Ÿäÿ± ŸÖÿµŸÜŸÅ'}
                      </span>
                    </td>
                    <td>
                      <div className="branch-info">
                        <span className="branch-name">
                          {game.branch_name || branchStats[game.branch_id]?.branch_name || `ŸÅÿ±ÿπ ${game.branch_id}`}
                        </span>
                        <span className="branch-id">ID: {game.branch_id}</span>
                      </div>
                    </td>
                    <td className="text-right">{game.price_per_hour || Math.ceil((game.price_per_15min || 0) * 4)} ÿ¨.ŸÖ</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <div className="empty-state">
            <div className="empty-icon">üéÆ</div>
            <h3>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ£ŸÑÿπÿßÿ®</h3>
            <p>{selectedBranch !== 'all' ?
              `ŸÑŸÖ Ÿäÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ÿ£Ÿä ÿ£ŸÑÿπÿßÿ® ŸÅŸä ${selectedBranchData?.branch_name}` :
              'ŸÑŸÖ Ÿäÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ÿ£Ÿä ÿ£ŸÑÿπÿßÿ® ÿ®ÿπÿØ'}
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default GamesManagement;
