  // ==================== USE EFFECTS ====================

  useEffect(() => {
    if (user) {
      console.log('ğŸ‘¤ Ù…Ø¹Ù„ÙˆÙ…Ø§Øª Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù…:', {
        id: user.id,
