  }, [user]);

  // ==================== FUNCTIONS ====================

  // ğŸ”„ ØªØ­Ù…ÙŠÙ„ Ø¬Ù…ÙŠØ¹ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª
  const loadData = async () => {
    try {
      setLoading(true);
      console.log('ğŸ“¥ Ø¨Ø¯Ø¡ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª...');
      
      if (!user?.branch_id) return;
      
      const tasks = [
        loadBranchData(),
