        }
      } else {
        console.warn('âš ï¸ Ø®Ø·Ø£ ÙÙŠ Ø¬Ù„Ø¨ Ø§Ù„ÙØ±ÙˆØ¹:', response.message);
        createFallbackBranch();
      }
    } catch (error) {
      console.warn('âš ï¸ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„ÙØ±Ø¹:', error.message);
      createFallbackBranch();
    }
  } catch (error) {
    console.warn('âš ï¸ Ø®Ø·Ø£ Ø¹Ø§Ù… ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„ÙØ±Ø¹:', error.message);
    createFallbackBranch();
  }
};

  // ğŸ® ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨
  const loadGamesData = async () => {
    try {
      if (!user?.branch_id) {
        console.warn('âš ï¸ Ù„Ø§ ÙŠÙˆØ¬Ø¯ branch_id Ù„Ù„Ù…Ø³ØªØ®Ø¯Ù…');
        return;
      }
      
      console.log(`ğŸ® Ø¬Ø§Ø±ÙŠ ØªØ­Ù…ÙŠÙ„ Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„ÙØ±Ø¹ ${user.branch_id}...`);
      
      const response = await api.get(`/branches/${user.branch_id}/games`);
      
      console.log('ğŸ“Š Ø§Ø³ØªØ¬Ø§Ø¨Ø© Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', response);
      
      if (response.success && response.data) {
        const games = Array.isArray(response.data) ? response.data : 
                     response.data.games || response.data.items || [];
        
        setAvailableGames(games);
        console.log(`âœ… ØªÙ… ØªØ­Ù…ÙŠÙ„ ${games.length} Ù„Ø¹Ø¨Ø© Ù„Ù„ÙØ±Ø¹`);
        
        // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª
        setStats(prev => ({
          ...prev,
          available: games.filter(g => g.status === 'Ù…ØªØ§Ø­').length
        }));
      } else {
        console.error('âŒ Ø§Ø³ØªØ¬Ø§Ø¨Ø© ØºÙŠØ± ØµØ­ÙŠØ­Ø©:', response);
        setAvailableGames([]);
      }
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„ÙØ±Ø¹:', error);
      
      // ğŸ”§ Ø¨Ø¯ÙŠÙ„: Ø¬Ù„Ø¨ ÙƒÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø«Ù… ØªØµÙÙŠØªÙ‡Ø§ Ù…Ø­Ù„ÙŠØ§Ù‹
      try {
        console.log('ğŸ”„ Ø¬Ø§Ø±ÙŠ ØªØ¬Ø±Ø¨Ø© Ø§Ù„Ù…Ø³Ø§Ø± Ø§Ù„Ø¨Ø¯ÙŠÙ„...');
