          setAvailableGames(branchGames);
          console.log(`âœ… ØªÙ… ØªØ­Ù…ÙŠÙ„ ${branchGames.length} Ù„Ø¹Ø¨Ø© (Ù…Ù† Ø§Ù„Ù…Ø³Ø§Ø± Ø§Ù„Ø¨Ø¯ÙŠÙ„)`);
        }
