  // âœ… **Ø¥Ø¶Ø§ÙØ© Ø¯Ø§Ù„Ø© calculateTimeRemaining Ù‡Ù†Ø§**
  const calculateTimeRemaining = (rental) => {
    if (!rental.expected_end_time || rental.is_open_time) return null;
    
